// Realistic Dragon - Medium Complexity
// 3D Printable mythical dragon with wings, horns, scales and tail

// Overall dimensions: ~80mm long, 60mm wide, 50mm tall

color("darkgreen") 
union() {
    // Main body
    body();

    // Neck and head
    translate([20, 0, 15]) rotate([0, 10, 0]) head();

    // Wings
    translate([0, 0, 25]) left_wing();
    mirror([0, 1, 0]) translate([0, 0, 25]) left_wing();

    // Legs
    translate([15, 8, 0]) front_leg();
    translate([15, -8, 0]) mirror([0, 1, 0]) front_leg();
    translate([-15, 6, 0]) rear_leg();
    translate([-15, -6, 0]) mirror([0, 1, 0]) rear_leg();

    // Tail
    translate([-30, 0, 8]) tail();
}

// Body module - elongated with scales
module body() {
    difference() {
        // Main body shape
        scale([1.5, 0.8, 0.6]) sphere(d=30, $fn=32);

        // Belly flattening
        translate([0, 0, -10]) cube([40, 25, 20], center=true);
    }

    // Scale texture along back
    for(i = [-15:5:15]) {
        translate([i, 0, 8]) scale([0.8, 0.4, 0.3]) sphere(d=8);
    }
}

// Head module with snout, horns and eyes
module head() {
    union() {
        // Snout
        translate([8, 0, 0]) scale([1.2, 0.6, 0.6]) sphere(d=15, $fn=32);

        // Main head
        sphere(d=20, $fn=32);

        // Horns - two curved ones
        translate([0, 5, 10]) rotate([30, 0, 20]) cylinder(h=15, d1=5, d2=2);
        translate([0, -5, 10]) rotate([30, 0, -20]) cylinder(h=15, d1=5, d2=2);

        // Eyes
        color("red") {
            translate([2, 6, 5]) sphere(d=3);
            translate([2, -6, 5]) sphere(d=3);
        }

        // Nostrils
        translate([12, 2, 0]) rotate([90, 0, 0]) cylinder(h=3, d=2);
        translate([12, -2, 0]) rotate([90, 0, 0]) cylinder(h=3, d=2);
    }
}

// Wing module - membrane with bones
module left_wing() {
    color("darkolive") 
    union() {
        // Main wing membrane
        translate([0, 10, 0]) rotate([0, 0, 10]) {
            difference() {
                // Wing shape
                scale([1.5, 2, 0.2]) sphere(d=20, $fn=6);

                // Cut bottom flat
                translate([0, 0, -3]) cube([40, 40, 6], center=true);
            }
        }

        // Wing bones
        for(i = [0:30:90]) {
            rotate([0, 0, i]) 
            translate([0, 20, 0]) scale([0.3, 1, 0.3]) cylinder(h=20, d=2);
        }
    }
}

// Leg module - clawed feet
module front_leg() {
    color("forestgreen") 
    union() {
        // Upper leg
        translate([0, 0, 5]) rotate([30, 0, 0]) cylinder(h=15, d=6);

        // Lower leg
        translate([0, 0, -3]) rotate([60, 0, 0]) cylinder(h=12, d=5);

        // Foot
        translate([0, 0, -8]) rotate([90, 0, 0]) {
            // Main foot
            cylinder(h=8, d=6);

            // Toes with claws
            for(i = [-2:1:2]) {
                translate([i*2, 5, 0]) {
                    // Toe
                    cylinder(h=4, d=2);
                    // Claw
                    translate([0, 2, 0]) cone(h=3, d1=2, d2=0.5);
                }
            }
        }
    }
}

module rear_leg() {
    scale([1.2, 1.2, 1.2]) front_leg();
}

// Tail module - segmented and tapering
module tail() {
    color("darkgreen") 
    union() {
        // Tail segments getting smaller
        for(i = [0:5:30]) {
            translate([-i, 0, -i/5]) 
            scale([1-i/35, 1-i/35, 1-i/35]) 
            rotate([0, -i*2, 0]) 
            cylinder(h=6, d=8-i/3, center=true);

            // Tail spikes
            if(i % 10 == 0) {
                translate([-i, 0, 3-i/5]) rotate([0, 0, 90]) 
                cone(h=4, d1=2, d2=0.5);
            }
        }

        // Tail tip
        translate([-35, 0, -7]) cone(h=8, d1=6, d2=0);
    }
}

// Wyvern - Medium Complexity Realistic Style
// Wings, horns, tail spike, and reptilian features

$fn = 32;

module wyvern() {
    // Main body - elongated torso
    color("darkgreen") {
        translate([0, 0, 15]) {
            scale([1.8, 1, 1]) sphere(8);  // Chest
            translate([0, 0, -8]) cylinder(h=16, r1=8, r2=5);  // Torso
            translate([0, 0, -20]) cylinder(h=12, r1=5, r2=3);  // Tail base
        }
    }

    // Head
    color("forestgreen") {
        translate([17, 0, 25]) {
            scale([1.3, 0.7, 0.8]) sphere(6);  // Snout
            translate([-3, 0, 2]) sphere(5);  // Skull
        }
    }

    // Horns
    color("darkslategray") {
        for(side = [-1, 1]) {
            translate([15, side*4, 27]) {
                rotate([0, side*20, 0]) 
                    cylinder(h=8, r1=3, r2=1);
            }
        }
    }

    // Eyes
    color("red") {
        for(side = [-1, 1]) {
            translate([20, side*2.5, 26]) 
                sphere(1.5);
        }
    }

    // Wings - membrane supported by bones
    module wing() {
        color("darkolivegreen") {
            difference() {
                // Main wing membrane
                translate([0, 0, 5]) {
                    rotate([0, 90, 0]) 
                        scale([1, 0.3, 1]) 
                            cylinder(h=30, r1=20, r2=5);
                }

                // Cut wing shape
                for(i = [0:3]) {
                    translate([i*6, 0, -5]) 
                        cube([2, 30, 30], center=true);
                }
            }
        }

        // Wing bones
        color("darkslategray") {
            for(angle = [0, 30, 60]) {
                rotate([angle, 0, 0]) {
                    translate([0, 0, 5]) {
                        rotate([0, 90, 0]) 
                            cylinder(h=25, r1=0.8, r2=0.3);
                    }
                }
            }
        }
    }

    // Attach wings
    for(side = [-1, 1]) {
        translate([0, side*8, 22]) {
            rotate([0, 0, side*10]) {
                mirror([0, side, 0]) 
                    wing();
            }
        }
    }

    // Legs - two legs as wyverns have
    color("darkgreen") {
        for(side = [-1, 1]) {
            translate([5, side*6, 10]) {
                // Thigh
                rotate([0, -30, 0]) 
                    cylinder(h=12, r1=3, r2=2);

                // Lower leg
                translate([0, 0, -12]) {
                    rotate([45, 0, 0]) 
                        cylinder(h=10, r1=2, r2=1.5);
                }

                // Foot
                translate([0, 0, -22]) {
                    rotate([90, 0, 0]) {
                        scale([2, 1, 0.5]) 
                            sphere(2);
                    }
                }
            }
        }
    }

    // Tail - long with spike
    color("darkgreen") {
        translate([0, 0, -25]) {
            for(i = [0:8]) {
                translate([0, 0, -i*3]) {
                    scale([1, 1, 0.8]) 
                        cylinder(h=3, r1=3-i*0.3, r2=3-(i+1)*0.3);
                }
            }
        }
    }

    // Tail spike
    color("darkslategray") {
        translate([0, 0, -50]) {
            rotate([0, 180, 0]) 
                cylinder(h=5, r1=2, r2=0.5);
        }
    }

    // Scales pattern on back
    color("forestgreen") {
        for(i = [0:5]) {
            for(side = [-1, 1]) {
                translate([5-i*3, side*4, 22-i*2]) {
                    scale([1, 0.5, 0.5]) 
                        sphere(1.5);
                }
            }
        }
    }
}

// Scale entire model to 70mm length
scale(0.7) wyvern();

// Basilisk mythical creature - Realistic style with medium complexity
// 3D printable design with proper wall thickness and minimal overhangs

// Color definitions
$fn = 32;

module basilisk() {
    // Body - serpentine with lizard-like proportions
    color("darkgreen") union() {
        // Main body segments
        translate([0, 0, 10]) scale([1.2, 0.8, 0.6]) sphere(d=25);
        translate([0, 0, 18]) scale([1, 0.7, 0.5]) sphere(d=20);
        translate([0, 0, 25]) scale([0.8, 0.6, 0.4]) sphere(d=15);

        // Tail extending back
        hull() {
            translate([0, 0, 25]) sphere(d=8);
            translate([0, 0, 40]) sphere(d=4);
        }
    }

    // Head - rooster-like with serpent features
    color("forestgreen") union() {
        // Main head
        translate([0, 0, 35]) rotate([0, 20, 0]) scale([0.9, 0.7, 0.8]) sphere(d=12);

        // Crest - rooster comb
        for(i = [-2:2]) {
            translate([i*2, 0, 42]) scale([0.5, 1, 2]) cylinder(h=3, d=2);
        }

        // Eyes - glowing red
        color("red") {
            translate([3, 4, 38]) sphere(d=1.5);
            translate([-3, 4, 38]) sphere(d=1.5);
        }

        // Beak/snout
        hull() {
            translate([0, 5, 35]) sphere(d=3);
            translate([0, 8, 35]) sphere(d=2);
        }
    }

    // Wings - bat-like membrane wings
    color("darkolivegreen") union() {
        // Left wing
        translate([12, 0, 25]) rotate([0, 0, -30]) union() {
            // Wing structure
            cylinder(h=20, d=2);
            translate([0, 0, 20]) rotate([30, 0, 0]) cylinder(h=15, d=1.5);
            translate([0, 0, 20]) rotate([-30, 0, 0]) cylinder(h=15, d=1.5);

            // Wing membrane
            hull() {
                translate([0, 0, 0]) sphere(d=2);
                translate([5, 0, 15]) sphere(d=2);
                translate([8, 0, 25]) sphere(d=2);
                translate([5, 0, 35]) sphere(d=2);
                translate([0, 0, 20]) sphere(d=2);
            }
        }

        // Right wing (mirrored)
        translate([-12, 0, 25]) mirror([1, 0, 0]) rotate([0, 0, -30]) union() {
            cylinder(h=20, d=2);
            translate([0, 0, 20]) rotate([30, 0, 0]) cylinder(h=15, d=1.5);
            translate([0, 0, 20]) rotate([-30, 0, 0]) cylinder(h=15, d=1.5);

            hull() {
                translate([0, 0, 0]) sphere(d=2);
                translate([5, 0, 15]) sphere(d=2);
                translate([8, 0, 25]) sphere(d=2);
                translate([5, 0, 35]) sphere(d=2);
                translate([0, 0, 20]) sphere(d=2);
            }
        }
    }

    // Legs - lizard-like with claws
    color("olivedrab") union() {
        // Front legs
        for(side = [-1, 1]) {
            translate([side*8, 0, 15]) union() {
                cylinder(h=10, d=3);
                translate([0, 0, 10]) rotate([30, 0, 0]) cylinder(h=8, d=2.5);
                translate([0, 0, 18]) rotate([30, 0, 0]) cylinder(h=6, d=2);

                // Claws
                for(i = [-1, 0, 1]) {
                    translate([i*1, 0, 23]) scale([0.5, 0.5, 1]) cylinder(h=3, d=1);
                }
            }
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([side*6, 0, 5]) union() {
                cylinder(h=8, d=3.5);
                translate([0, 0, 8]) rotate([30, 0, 0]) cylinder(h=7, d=3);
                translate([0, 0, 15]) rotate([30, 0, 0]) cylinder(h=6, d=2.5);

                // Claws
                for(i = [-1.2, 0, 1.2]) {
                    translate([i*1.2, 0, 21]) scale([0.6, 0.6, 1]) cylinder(h=3, d=1.2);
                }
            }
        }
    }

    // Scales - overlapping pattern
    color("darkgreen") {
        for(i = [0:3]) {
            for(j = [0:8]) {
                if(j % 2 == i % 2) {
                    translate([sin(j*30)*2, cos(j*30)*2, 12+i*4]) 
                    rotate([0, 0, j*30]) 
                    scale([1, 0.5, 0.3]) 
                    sphere(d=3);
                }
            }
        }
    }

    // Horns - small curved horns
    color("saddlebrown") {
        for(side = [-1, 1]) {
            translate([side*4, 0, 40]) rotate([0, side*20, 0]) 
            cylinder(h=6, d1=2, d2=1);
            translate([side*5, 0, 44]) rotate([0, side*30, 0]) 
            cylinder(h=4, d1=1.5, d2=0.5);
        }
    }
}

// Generate the basilisk
translate([0, 0, 0]) scale([2, 2, 2]) basilisk();

// Realistic Hydra - Medium Complexity
// Five-headed serpentine dragon with scales and wings
// Designed for 3D printing at 80mm length

// Overall scale factor
scale_factor = 0.8;

// Colors
color_body = "#2E8B57";
color_scales = "#006400";
color_eyes = "#DC143C";
color_teeth = "#FFFACD";
color_wings = "#556B2F";

module hydra() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Central torso
            color(color_body) {
                translate([0, 0, 15]) {
                    scale([1.5, 1, 1]) {
                        sphere(12);
                    }
                }

                // Tail
                translate([-25, 0, 8]) {
                    rotate([0, 30, 0]) {
                        cylinder(h = 35, r1 = 8, r2 = 3);
                    }
                }

                // Legs (4)
                for(i = [0:1]) {
                    mirror([i*2-1, 0, 0]) {
                        translate([8, 6, 5]) {
                            rotate([0, 0, -30]) {
                                cylinder(h = 15, r1 = 4, r2 = 3);
                            }
                        }
                        translate([12, -6, 5]) {
                            rotate([0, 0, 30]) {
                                cylinder(h = 15, r1 = 4, r2 = 3);
                            }
                        }
                    }
                }
            }

            // Wings
            color(color_wings) {
                translate([0, 0, 25]) {
                    for(side = [0:1]) {
                        mirror([side*2-1, 0, 0]) {
                            rotate([0, 0, -20 + side*40]) {
                                translate([8, 0, 0]) {
                                    scale([2, 0.5, 1.5]) {
                                        sphere(8);
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // Five necks and heads
            color(color_body) {
                // Central head
                translate([18, 0, 25]) {
                    rotate([0, -15, 0]) {
                        cylinder(h = 15, r1 = 5, r2 = 4);
                        translate([0, 0, 15]) {
                            head(0);
                        }
                    }
                }

                // Four surrounding heads
                for(angle = [0:3]) {
                    rotate([0, 0, angle*90]) {
                        translate([15, 0, 22]) {
                            rotate([10, -20, 0]) {
                                cylinder(h = 12, r1 = 4, r2 = 3);
                                translate([0, 0, 12]) {
                                    head(angle*90);
                                }
                            }
                        }
                    }
                }
            }

            // Scales on body
            color(color_scales) {
                for(i = [0:19]) {
                    rotate([0, 0, i*18]) {
                        translate([0, 0, 15]) {
                            scale([1.6, 1.1, 1.1]) {
                                sphere(1.5);
                            }
                        }
                    }
                }
            }
        }
    }
}

module head(rotation) {
    union() {
        // Head
        scale([1.2, 0.8, 1]) {
            sphere(5);
        }

        // Snout
        translate([5, 0, 0]) {
            scale([1.5, 0.6, 0.6]) {
                sphere(3);
            }
        }

        // Eyes
        color(color_eyes) {
            for(side = [0:1]) {
                mirror([0, side*2-1, 0]) {
                    translate([3, 2.5, 1]) {
                        sphere(1);
                    }
                }
            }
        }

        // Teeth
        color(color_teeth) {
            for(i = [0:3]) {
                translate([5.5, -1.5 + i, -1]) {
                    rotate([0, 0, 90]) {
                        cylinder(h = 2, r1 = 0.5, r2 = 0.2);
                    }
                }
            }
        }

        // Horns
        for(side = [0:1]) {
            mirror([0, side*2-1, 0]) {
                translate([0, 3, 2]) {
                    rotate([0, -30, 0]) {
                        cylinder(h = 4, r1 = 1, r2 = 0.5);
                    }
                }
            }
        }
    }
}

// Render the hydra
hydra();

// Naga - Mythical Serpent Creature
// Realistic style with moderate complexity

// Color definitions
color_body = "forestgreen";
color_scales = "darkgreen";
color_eyes = "red";
color_hood = "limegreen";
color_fangs = "ivory";

// Parameters
$fn = 32;
body_segments = 8;
scale_size = 1.5;
scale_spacing = 3;

// Main module
module naga() {
    union() {
        // Serpent body
        color(color_body) {
            // Head
            translate([0, 0, 40]) {
                rotate([0, 0, 90]) {
                    // Main head shape
                    scale([1.2, 1, 1]) sphere(8);

                    // Snout
                    translate([9, 0, 0]) {
                        scale([1.5, 0.8, 0.8]) sphere(5);
                    }

                    // Hood (cobra-like)
                    color(color_hood) {
                        translate([-5, 0, 2]) {
                            rotate([0, 15, 0]) {
                                scale([2, 3, 0.5]) sphere(6);
                            }
                        }
                        translate([-5, 0, -2]) {
                            rotate([0, -15, 0]) {
                                scale([2, 3, 0.5]) sphere(6);
                            }
                        }
                    }
                }
            }

            // Eyes
            color(color_eyes) {
                translate([6, 3.5, 3]) sphere(1.5);
                translate([6, -3.5, 3]) sphere(1.5);
            }

            // Fangs
            color(color_fangs) {
                translate([10, 2, -1]) {
                    rotate([0, -20, 0]) cylinder(4, 1, 0.2);
                }
                translate([10, -2, -1]) {
                    rotate([0, -20, 0]) cylinder(4, 1, 0.2);
                }
            }
        }

        // Serpent body coils
        color(color_body) {
            for(i = [0:body_segments-1]) {
                translate([0, 0, 35 - i * 5]) {
                    rotate([0, 0, i * 20]) {
                        scale([1 - i * 0.05, 1 - i * 0.05, 1 - i * 0.05]) {
                            sphere(7 - i * 0.5);
                        }
                    }
                }
            }
        }

        // Body segments with scales
        for(i = [0:body_segments*2-1]) {
            translate([0, 0, 30 - i * 2.5]) {
                rotate([0, 0, i * 15]) {
                    scale([0.9 - i * 0.03, 0.9 - i * 0.03, 0.9 - i * 0.03]) {
                        // Main body segment
                        color(color_body) {
                            sphere(6 - i * 0.3);
                        }

                        // Scales pattern
                        color(color_scales) {
                            for(j = [0:5]) {
                                rotate([0, 0, j * 60]) {
                                    translate([3 + sin(i * 30) * 1, 0, 0]) {
                                        scale([scale_size, scale_size * 0.7, scale_spacing * 0.3]) {
                                            sphere(1);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        // Tail
        color(color_body) {
            translate([0, 0, -10]) {
                for(i = [0:10]) {
                    translate([sin(i * 20) * 5, cos(i * 20) * 5, -i * 2]) {
                        scale([1 - i * 0.08, 1 - i * 0.08, 1 - i * 0.08]) {
                            sphere(4 - i * 0.3);
                        }
                    }
                }
            }
        }

        // Decorative crest
        color(color_hood) {
            translate([0, 0, 42]) {
                rotate([0, 90, 0]) {
                    for(i = [0:4]) {
                        translate([0, 0, i * 2 - 4]) {
                            rotate([i * 10, 0, 0]) {
                                scale([0.5, 0.2, 1]) cylinder(3, 1, 0.5);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Create the naga
naga();

// Realistic Drake (Medium Complexity)
// 3D printable mythical creature with wings, horns, scales and tail
// Approx 80mm long, 50mm tall

// Main color scheme
color("darkolivegreen") {

// Body
difference() {
    union() {
        // Main torso
        scale([1.2,0.8,1]) sphere(d=30, $fn=32);

        // Neck
        translate([0,12,5]) rotate([0,-30,0]) 
            scale([0.7,0.7,1.3]) cylinder(d1=12, d2=8, h=20, $fn=16);

        // Head
        translate([0,25,15]) rotate([0,-30,0])
            scale([1.2,0.9,1.1]) sphere(d=15, $fn=24);
    }

    // Eye sockets
    translate([5,27,17]) rotate([0,-30,0]) sphere(d=4, $fn=12);
    translate([-5,27,17]) rotate([0,-30,0]) sphere(d=4, $fn=12);

    // Nostrils
    translate([2,32,14]) rotate([0,-30,0]) cylinder(d=2, h=5, $fn=8);
    translate([-2,32,14]) rotate([0,-30,0]) cylinder(d=2, h=5, $fn=8);
}

// Eyes
color("firebrick") {
    translate([5,27,17]) rotate([0,-30,0]) sphere(d=3, $fn=8);
    translate([-5,27,17]) rotate([0,-30,0]) sphere(d=3, $fn=8);
}

// Horns
module horn() {
    color("dimgray") {
        rotate([0,-20,0]) cylinder(d1=6, d2=2, h=12, $fn=8);
        translate([0,0,10]) rotate([0,-20,0]) sphere(d=3, $fn=8);
    }
}

translate([7,20,22]) rotate([0,0,30]) horn();
translate([-7,20,22]) rotate([0,0,-30]) mirror([1,0,0]) horn();

// Scales pattern on back
color("forestgreen") {
    for(i=[0:5]) {
        translate([0, -15+i*5, 10+sin(i*30)*2]) 
            scale([0.8,0.4,0.3]) sphere(d=6, $fn=16);
    }
}

// Wings
module wing() {
    color("darkslategray") {
        translate([0,0,-2]) 
            rotate([0,0,0]) 
            scale([1,1.5,0.1]) 
            sphere(d=30, $fn=24);

        // Wing bones
        for(i=[0:30:150]) {
            rotate([0,0,i]) 
            translate([10,0,0]) 
            scale([0.1,1.2,0.1]) 
            cylinder(d=2, h=25, $fn=6);
        }
    }
}

translate([15,-5,8]) rotate([0,0,30]) wing();
translate([-15,-5,8]) rotate([0,0,-30]) mirror([1,0,0]) wing();

// Legs
module leg() {
    color("darkolivegreen") {
        // Upper leg
        rotate([0,0,0]) cylinder(d=6, h=15, $fn=12);
        // Lower leg
        translate([0,0,-15]) rotate([0,15,0]) 
            cylinder(d=5, h=12, $fn=12);
        // Foot
        translate([0,-5,-25]) rotate([0,15,0]) 
            scale([1.5,2,0.5]) cube([5,8,3], center=true);
    }
}

translate([8,-10,-10]) leg();
translate([-8,-10,-10]) mirror([1,0,0]) leg();

// Tail
color("darkolivegreen") {
    translate([0,-20,-5]) rotate([0,20,0]) 
        scale([1,1,0.8]) cylinder(d1=12, d2=4, h=25, $fn=16);

    // Tail spikes
    color("dimgray") {
        for(i=[0:5:20]) {
            translate([0,-20-i, -5+sin(i*10)*3]) 
                rotate([0,0,i*5]) 
