// Wyrm - Serpentine Dragon
// Realistic style, medium complexity, 3D printable

$fn = 32;

// Main colors
body_color = "darkolivegreen";
accent_color = "goldenrod";
eye_color = "red";

module scaled_sphere(r, s, v) {
    scale([s, s, v]) sphere(r);
}

// Scale pattern module
module scale_pattern() {
    for(i = [0:5]) {
        rotate([0, 0, i * 60]) 
            translate([2, 0, -0.5])
                rotate([90, 0, 0])
                    scaled_sphere(1.2, 1.5, 0.3);
    }
}

// Head module
module wyrm_head() {
    color(body_color) {
        // Main head shape
        translate([0, 0, 3])
            scaled_sphere(8, 1.2, 0.8);

        // Snout
        translate([10, 0, 2])
            rotate([0, -10, 0])
                scaled_sphere(5, 1.5, 0.7);

        // Horns
        color(accent_color) {
            translate([-2, 4, 8])
                rotate([0, 20, 10])
                    cylinder(h = 6, r1 = 2, r2 = 0.5);
            translate([-2, -4, 8])
                rotate([0, -20, -10])
                    cylinder(h = 6, r1 = 2, r2 = 0.5);
        }

        // Eyes
        color(eye_color) {
            translate([6, 3.5, 5])
                sphere(1.5);
            translate([6, -3.5, 5])
                sphere(1.5);
        }

        // Nostrils
        difference() {
            translate([10, 2, 2])
                sphere(1);
            translate([11, 2, 2])
                sphere(1.2);
        }
        difference() {
            translate([10, -2, 2])
                sphere(1);
            translate([11, -2, 2])
                sphere(1.2);
        }
    }
}

// Body segment module
module body_segment(pos, angle, scale_factor) {
    translate(pos)
        rotate([0, 0, angle])
            scale([scale_factor, scale_factor, scale_factor]) {
                color(body_color) {
                    scaled_sphere(10, 1, 0.4);
                }
                // Add scales to each segment
                scale_pattern();
            }
}

// Create wyrm body
module wyrm_body() {
    // Head first
    wyrm_head();

    // Body segments
    segments = 8;
    for(i = [1:segments]) {
        angle = i * 25;
        x = -i * 12 * cos(angle);
        y = -i * 12 * sin(angle);
        z = 3 - i * 0.5;
        scale_factor = 1 - i * 0.08;
        body_segment([x, y, z], angle, scale_factor);
    }

    // Tail
    color(body_color)
        translate([-segments * 12 * cos(segments * 25) - 5, 
                  -segments * 12 * sin(segments * 25), 
                  3 - segments * 0.5 - 3])
            rotate([0, 0, segments * 25])
                cylinder(h = 15, r1 = 2, r2 = 0.5);
}

