            }

            // Snout
            translate([24, 0, 26]) {
                rotate([-10, 0, 0])
                    cylinder(h=8, r1=4, r2=2);
            }

            // Tail
            difference() {
                translate([-12, 0, 8]) {
                    rotate([0, 0, 180])
                        cylinder(h=25, r1=6, r2=2);
                }
                // Tail flame cavity
                translate([-12, 0, 8]) {
                    rotate([0, 0, 180])
                        cylinder(h=20, r1=4, r2=0.5);
                }
            }
        }

        // Legs
        for(side = [-1, 1]) {
            // Front legs
            translate([6, side * 5, 8]) {
                rotate([0, side * 15, 0])
                    cylinder(h=12, r=2);
                translate([0, 0, -6])
                    sphere(r=2.5);
            }

            // Back legs
            translate([-6, side * 5, 8]) {
                rotate([0, side * 15, 0])
                    cylinder(h=12, r=2.5);
                translate([0, 0, -6])
                    sphere(r=3);
            }
        }

        // Wings
        color([0.3, 0.05, 0.02]) {
            for(side = [-1, 1]) {
                translate([0, side * 8, 18]) {
                    rotate([0, 0, side * 30])
                        scale([1.5, 0.8, 0.1])
                            sphere(r=10);
                }

                // Wing membrane support
                for(i = [0:2]) {
                    translate([0, side * (8 + i * 4), 18 - i * 3]) {
                        rotate([0, 0, side * (30 - i * 10)])
                            cylinder(h=12 - i * 3, r=0.5);
                    }
                }
            }
        }

        // Horns
        color([0.3, 0.2, 0.1]) {
            for(side = [-1, 1]) {
                translate([20, side * 3, 30]) {
                    rotate([0, side * 20, 0])
                        cylinder(h=6, r1=1.5, r2=0.5);
                }
            }
        }

        // Eyes
        color([1, 0.8, 0]) {
            for(side = [-1, 1]) {
                translate([22, side * 3, 28]) {
                    sphere(r=1.5);
                }
            }
        }

        // Scales pattern
        color([0.5, 0.08, 0.03]) {
            for(i = [0:8]) {
                for(j = [0:4]) {
                    translate([15 - i * 4, 0, 12 + j * 2]) {
                        rotate([0, 90, 0])
                            scale([0.5, 0.3, 0.1])
                                cylinder(h=2, r=1);
                    }
                }
            }
        }
    }

    // Flame tail effect
    color([1, 0.3, 0]) {
        translate([-12, 0, 8]) {
            rotate([0, 0, 180])
                scale([0.8, 0.8, 1])
                    cylinder(h=15, r1=2, r2=0);
        }
    }
}

