                // Wing arm
                translate([0, 0, 0]) 
                    rotate([90, 0, side==0 ? -30 : 30]) 
                        cylinder(h=15, d=2, $fn=8);

                // Wing membrane supports
                for(j = [0:2]) {
                    rotate([0, 0, j*30 - 30]) 
                        translate([12*cos(j*30 - 30), 0, 12*sin(j*30 - 30)]) 
                            cylinder(h=2, d=1, $fn=6);
                }

                // Wing membrane
                color("darkslateblue") {
                    translate([-1, 0, 0]) 
                        rotate([90, 0, side==0 ? -20 : 20]) {
