            }
        }
    }

    // Assemble the creature
    body();
    head();
    horns();
    wings();
    legs();
    tail();
    scales();
}

// Sea Serpent - Realistic Style, Medium Complexity
// Designed for 3D printing with proper wall thickness

// Colors
color_sea = "#2E8B57";
color_belly = "#B0E0E6";
color_eye = "#FF4500";
color_fin = "#4682B4";

// Main body module
module sea_serpent() {
    color(color_sea) {
        // Head
        translate([0, 0, 15]) {
            union() {
                // Main head shape
                scale([1.2, 0.8, 1]) sphere(r=8);

                // Snout
                translate([8, 0, 0]) 
                    scale([2, 0.6, 0.6]) sphere(r=5);

                // Horns
                translate([3, 0, 6]) 
                    rotate([0, -15, 0]) 
                    cylinder(h=12, r1=2, r2=0.5);
                translate([3, 0, -6]) 
                    rotate([0, 15, 0]) 
                    cylinder(h=12, r1=2, r2=0.5);

                // Eyes
                color(color_eye) {
                    translate([5, 4, 3]) sphere(r=1.5);
                    translate([5, -4, 3]) sphere(r=1.5);
                }
            }
        }

        // Neck and body segments
        for(i = [0:8]) {
            color(i % 2 == 0 ? color_sea : color_belly) {
                translate([0, 0, 15 - i * 6]) {
                    rotate([0, 0, i * 15]) {
                        scale([1 - i * 0.05, 1 - i * 0.05, 1 - i * 0.05]) {
                            // Body segment
                            hull() {
                                translate([0, 0, 0]) sphere(r=7 - i * 0.5);
                                translate([0, 0, -6]) sphere(r=6 - i * 0.4);
                            }

                            // Scales pattern
                            for(j = [0:3]) {
                                rotate([0, 0, j * 90]) {
                                    translate([5 - i * 0.3, 0, -3]) {
                                        scale([0.8, 0.5, 0.5]) sphere(r=1.5);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        // Dorsal fin
        color(color_fin) {
            translate([-5, 0, 15]) {
                for(i = [0:6]) {
                    translate([0, 0, -i * 2.5]) {
                        rotate([0, 90, 0]) {
                            scale([1 - i * 0.1, 1 - i * 0.1, 1 - i * 0.1]) {
                                linear_extrude(height=0.5) {
                                    polygon(points=[[0,0], [8,0], [4,3], [0,0]]);
                                }
                            }
                        }
                    }
                }
            }
        }

        // Tail
        translate([0, 0, -35]) {
            rotate([0, 0, 45]) {
                // Tail segments
                for(i = [0:4]) {
                    translate([0, 0, -i * 5]) {
                        rotate([0, 0, i * 20]) {
                            scale([0.6 - i * 0.1, 0.6 - i * 0.1, 0.6 - i * 0.1]) {
                                sphere(r=5 - i);

                                // Tail fin
                                if(i == 4) {
                                    color(color_fin) {
                                        rotate([0, 90, 0]) {
                                            linear_extrude(height=0.5) {
                                                polygon(points=[[0,0], [15,0], [7,8], [0,0]]);
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        // Side fins
        color(color_fin) {
            translate([0, 8, 5]) {
                rotate([0, 45, 0]) {
                    scale([1.5, 1, 0.5]) {
                        sphere(r=4);
                    }
                }
            }
            translate([0, -8, 5]) {
                rotate([0, -45, 0]) {
                    scale([1.5, 1, 0.5]) {
                        sphere(r=4);
                    }
                }
            }
        }
    }
}

// Base for stability
module base() {
    color(color_sea) {
        translate([0, 0, -45]) {
            cylinder(h=2, r=20);
        }
    }
}

// Create the model
union() {
    sea_serpent();
    base();
}

// Scale for 3D printing (total length ~80mm)
scale([0.8, 0.8, 0.8]) {
    sea_serpent();
    base();
}

// SkyHydra - A mythical multi-headed aerial dragon
// Wingspan: 80mm, Length: 60mm, Height: 35mm
// Designed for 3D printing with no overhangs >45

$fn = 32;

// Main body
module skyhydra() {
    // Central torso
    color("steelblue") {
        translate([0, 0, 8]) 
            scale([1.2, 1, 1]) 
                sphere(r=12);

        // Tail section
        translate([0, -25, 5]) 
            cylinder(h=25, r1=10, r2=4);
    }

    // Three dragon heads
    for(i = [-1, 0, 1]) {
        translate([i*15, 20, 12]) 
            rotate([0, 0, i*15]) 
                dragon_head();
    }

    // Four wings (two pairs)
    for(side = [-1, 1]) {
        translate([side*12, -5, 15]) 
            rotate([0, side*20, 0]) 
                wing(side);

        translate([side*8, -15, 10]) 
            rotate([0, side*30, 10]) 
                wing_small(side);
    }

    // Legs
    for(side = [-1, 1]) {
        translate([side*8, 0, 0]) 
            dragon_leg();
    }

    // Scales pattern on back
    scales_pattern();

    // Cloud-like magical aura
    magical_aura();
}

// Dragon head module
module dragon_head() {
    color("slateblue") {
        union() {
            // Snout
            translate([0, 5, 0]) 
                scale([1, 1.5, 0.8]) 
                    sphere(r=6);

            // Head
            sphere(r=8);

            // Horns
            translate([0, -3, 8]) 
                rotate([30, 0, 0]) 
                    cylinder(h=10, r1=3, r2=1);

            // Eyes
            translate([3, 2, 2]) 
                color("gold") 
                    sphere(r=2);

            // Nostrils
            translate([0, 8, -1]) {
                translate([2, 0, 0]) 
                    sphere(r=1);
                translate([-2, 0, 0]) 
                    sphere(r=1);
            }
        }
    }
}

// Wing module
module wing(side) {
    color("cornflowerblue") {
        difference() {
            // Main wing membrane
            translate([0, 0, 0]) 
                rotate([0, 0, side*15]) 
                    scale([1, 2.5, 0.1]) 
                        sphere(r=15);

            // Wing holes for lightness
            for(i = [1:3]) {
                translate([side*i*4, i*5, -1]) 
                    rotate([0, 0, side*i*10]) 
                        scale([0.8, 1.2, 1]) 
                            cylinder(h=3, r=2);
            }
        }

        // Wing bones
        color("midnightblue") {
            translate([0, 0, 0]) 
                rotate([0, 0, side*15]) 
                    cylinder(h=30, r=0.8);

            translate([0, 15, 0]) 
                rotate([0, 0, side*45]) 
                    cylinder(h=20, r=0.6);
        }
    }
}

// Smaller secondary wings
module wing_small(side) {
    scale(0.7) 
        wing(side);
}

// Dragon leg module
module dragon_leg() {
    color("royalblue") {
        union() {
            // Thigh
            translate([0, 0, 5]) 
                rotate([0, 10, 0]) 
                    cylinder(h=12, r=3);

            // Lower leg
            translate([0, 0, 0]) 
                rotate([0, -20, 0]) 
                    cylinder(h=10, r1=3, r2=2);

            // Foot
            translate([0, -3, -3]) 
                rotate([0, -90, 0]) 
                    scale([1.5, 1, 0.5]) 
                        sphere(r=4);

            // Claws
            for(i = [-1, 0, 1]) {
                translate([i*2, -5, -2]) 
                    rotate([0, -20, 0]) 
                        cone(h=3, r1=1, r2=0.3);
            }
        }
    }
}

// Scales pattern on back
module scales_pattern() {
    color("darkblue") {
        for(y = [-20:5:15]) {
            for(x = [-10:5:10]) {
                if(x*x + y*y < 100) {
                    translate([x, y, 18]) 
                        scale([1, 1, 0.3]) 
                            sphere(r=2);
                }
            }
        }
    }
}

// Magical cloud aura
module magical_aura() {
    color("lightskyblue", 0.3) {
        for(i = [1:8]) {
            rotate([0, 0, i*45]) 
                translate([25, 0, 15]) 
                    scale([1.2, 1, 0.8]) 
                        sphere(r=5);
        }
    }
}

// Cone helper module
module cone(h, r1, r2) {
    cylinder(h=h, r1=r1, r2=r2);
}

// Main assembly
skyhydra();

// Shadowwyrm  medium-complexity realistic 3-D printable miniature
// Overall length  90 mm, wingspan  85 mm
// All wall thickness  0.8 mm, supports only under base plate

module body() {
    difference() {
        // Torso
        translate([0,0,12])
            scale([1,0.65,1])
            sphere(r=12,$fn=40);

        // Neck cavity
        translate([0,0,30])
            rotate([0,20,0])
            cylinder(h=15,r1=6,r2=4,$fn=20);

        // Tail socket cavity
        translate([0,0,-5])
            rotate([0,-10,0])
            cylinder(h=12,r1=5,r2=7,$fn=20);
    }
}

module neck() {
    translate([0,0,30])
        rotate([0,20,0])
        cylinder(h=18,r1=4,r2=6,$fn=20);
}

module head() {
    translate([0,0,48]) {
        difference() {
            // Snout
            rotate([0,20,0])
            cylinder(h=20,r1=6,r2=2,$fn=20);

            // Nostrils
            for(side=[-1,1])
                translate([side*4,0,48])
                    rotate([0,20,0])
                    cylinder(h=6,r=1,$fn=8);

            // Eye sockets
            for(side=[-1,1])
                translate([side*5,5,52])
                    sphere(r=2.5,$fn=12);
        }

        // Eyes
        color("red") 
        for(side=[-1,1])
            translate([side*5,5,52])
                sphere(r=1.5,$fn=8);

        // Horns
        color("dimgray")
        for(side=[-1,1])
            translate([side*4,-4,58])
                rotate([side*-20,0,side*-15])
                cylinder(h=12,r1=2.5,r2=0.5,$fn=8);
    }
}

module wing() {
    // Membrane wing using thin intersected prisms
    difference() {
        intersection() {
            scale([1,5,0.8])
                sphere(r=16,$fn=30);
            translate([0,0,-2])
                cube([40,80,4],center=true);
        }
        // Veins cutouts
        for(i=[-2:2])
            translate([0,i*8+4,0])
                rotate([0,0,i*5])
                cube([40,1,2],center=true);
    }
}

module wings() {
    color("darkslategray")
    for(side=[-1,1])
        translate([side*12,0,25])
            rotate([0,side*10,side*30])
                wing();
}

module tail() {
    color("darkslategray")
    translate([0,0,-5])
        rotate([0,-10,0])
        for(i=[0:6]) {
            translate([0,0,-i*7])
                scale([1-i*0.08,1-i*0.08,1])
                sphere(r=7-i,$fn=20);
        }
}

module legs() {
    color("dimgray")
    for(side=[-1,1])
        for(pos=[0,1]) {
            translate([side*8,0,10-pos*4])
                rotate([0,0,side*10])
                cylinder(h=12-pos*2,r1=3,r2=2,$fn=8);
        }
}

module base() {
    color("gray")
    translate([0,0,-15])
        cylinder(h=3,r1=25,r2=30,$fn=60);
}

// Assembly
union() {
    base();
    body();
    neck();
    head();
    wings();
    tail();
    legs();
}

// Starlizard - A mythical celestial lizard creature
// Features: Star-pattern wings, crystal horns, segmented tail with star tip

// Colors
color_stars = [1, 0.8, 0];        // Golden stars
color_scales = [0.2, 0.4, 0.6];   // Deep blue scales
color_crystals = [0.5, 0.8, 1];   // Light blue crystals

// Main parameters
body_length = 40;
body_width = 20;
body_height = 15;
wing_span = 60;
tail_segments = 5;
scale_size = 2;

// Star primitive module
module star(radius, height) {
    linear_extrude(height) {
        union() {
            circle(r=radius, $fn=5);
            rotate([0, 0, 36]) circle(r=radius, $fn=5);
        }
    }
}

// Crystal horn module
module crystal_horn(base, height) {
    color(color_crystals) {
        hull() {
            translate([0, 0, 0]) cylinder(h=base, r=base/2, $fn=6);
            translate([0, 0, height]) cylinder(h=base/3, r=base/4, $fn=6);
        }
    }
}

// Scale pattern module
module scale_pattern() {
    color(color_scales) {
        for (i = [-1:1]) {
            for (j = [-2:2]) {
                if (i != 0 || j != 0) {
                    translate([i*scale_size*2, j*scale_size*1.5, 0]) 
                        sphere(r=scale_size, $fn=8);
                }
            }
        }
    }
}

// Main body
module body() {
    color(color_scales) {
        // Main torso
        hull() {
            sphere(r=body_width/2, $fn=20);
            translate([body_length/2, 0, 0]) sphere(r=body_width/2.5, $fn=20);
            translate([-body_length/3, 0, 0]) sphere(r=body_width/2.5, $fn=20);
        }

        // Apply scales
        for (i = [1:5]) {
            translate([i*body_length/6 - body_length/2, 0, body_height/2]) 
                scale_pattern();
        }
    }
}

// Wings with star patterns
module wing() {
    color(color_stars) {
        // Wing membrane
        translate([0, 0, 0]) {
            rotate([0, 90, 0]) {
                linear_extrude(1) {
                    polygon(points=[
                        [0, 0], [wing_span/2, 0], [wing_span/3, wing_span/3],
                        [wing_span/4, wing_span/2], [0, wing_span/3]
                    ]);
                }
            }
        }

        // Star decorations
        for (i = [1:3]) {
            translate([i*wing_span/8, i*wing_span/8, 1]) {
                rotate([90, 0, 0]) star(3, 1);
            }
        }

        // Wing bone structure
        color([0.8, 0.8, 0.8]) {
            cylinder(h=wing_span/2, r=1, $fn=8);
            rotate([0, 45, 0]) cylinder(h=wing_span/2.5, r=1, $fn=8);
        }
    }
}

// Head with horns
module head() {
    color(color_scales) {
        // Snout
        translate([body_length/2 + 5, 0, 0]) {
            hull() {
                sphere(r=8, $fn=16);
                translate([5, 0, 0]) sphere(r=6, $fn=16);
            }

            // Eyes
            color([0.2, 0.2, 0.2]) {
                translate([8, 4, 3]) sphere(r=1.5, $fn=8);
                translate([8, -4, 3]) sphere(r=1.5, $fn=8);
            }

            // Crystal horns
            translate([2, 6, 8]) crystal_horn(3, 10);
            translate([2, -6, 8]) crystal_horn(3, 10);
        }
    }
}

// Segmented tail with star tip
module tail() {
    color(color_scales) {
        for (i = [0:tail_segments-1]) {
            translate([-i*body_length/4 - body_length/3, 0, -i*2]) {
                scale([1 - i*0.1, 1 - i*0.1, 1 - i*0.1]) {
                    sphere(r=body_width/2.5 - i, $fn=12);
                }
            }
        }

