deep_purple = [0.3, 0.1, 0.5, 0.9];
crystal = [0.8, 0.7, 1.0, 0.6];

// Main body
module amethyst_serpent() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Serpentine body segments
            body_segments();

            // Head with crystal crown
            translate([0, 0, 35]) rotate([0, 20, 0]) head();

            // Wings (folded for printing)
            translate([-5, -8, 25]) rotate([0, -10, -30]) wing();
            translate([5, -8, 25]) mirror([1, 0, 0]) rotate([0, -10, -30]) wing();

