// Body made of overlapping crystal scales
module body_segments() {
    for(i = [0:5]) {
        translate([0, 0, 30 - i * 6]) {
            rotate([0, 0, i * 15]) {
                color(amethyst) 
                scale([1 - i*0.05, 1 - i*0.05, 1]) 
                crystal_segment(12 - i, 8 - i*0.5);
            }
        }
    }
}

// Individual crystal scale segment
module crystal_segment(width, height) {
    difference() {
        // Main scale body
        union() {
            cylinder(h=height, r1=width, r2=width*0.8);
            // Crystal facets
            for(angle = [0:60:360]) {
                rotate([0, 0, angle]) 
                translate([width*0.7, 0, height*0.3]) 
                scale([0.3, 0.1, 0.5]) 
                cube([width, width, height], center=true);
            }
        }
        // Hollow center for material saving
        translate([0, 0, -1]) 
        cylinder(h=height+2, r1=width*0.4, r2=width*0.3);
    }
}

