                // Wing support ribs
                for(i = [1:3]) {
                    rotate([0, 0, i * 30 - 45]) {
                        translate([0, 0, i * 3]) 
                        scale([0.3, 0.3, 1.5]) 
                        cylinder(h = 8, r1 = 2, r2 = 1);
                    }
                }
            }
        }
    }
}

module head() {
    // Fluid mercury head with horns
    union() {
        // Main head - elongated sphere
        scale([1.2, 1.5, 1]) sphere(r = 8);

        // Snout
        translate([0, -10, 0]) 
        scale([0.8, 1.2, 0.8]) 
        sphere(r = 6);

        // Curved horns made of flowing metal
        for(side = [-1, 1]) {
            translate([side * 5, 5, 5]) {
                rotate([side * 20, 0, -30]) {
                    // Spiral horn
                    for(h = [0:5]) {
                        translate([0, 0, h * 2]) 
                        rotate([0, 0, h * 45]) 
                        scale([0.8, 0.8, 1]) 
                        cylinder(h = 3, r = 2.5 - h * 0.3);
                    }
                }
            }
        }

        // Eyes - glowing mercury pools
        for(side = [-1, 1]) {
            translate([side * 4, -7, 2]) {
                color(mercury_blue) sphere(r = 2);
