// Abyssal Serpent - 3D Printable
// Medium complexity realistic style

$fn = 32;
scale_factor = 1;

module segment(r1, r2, h) {
    cylinder(h=h, r1=r1, r2=r2, center=true);
}

module fin(x, y, z, rot) {
    translate([x, y, z])
    rotate([0, 0, rot])
    scale([1, 0.3, 0.1])
    sphere(3);
}

module eye() {
    color("crimson")
    union() {
        sphere(1.5);
        translate([0.5, 0, 1])
        scale([0.5, 0.5, 0.2])
        sphere(1);
    }
}

module head() {
    color("darkslategray")
    union() {
        // Main head
        scale([1.5, 1, 1])
        sphere(8);

        // Snout
        translate([10, 0, 0])
        scale([2, 0.8, 0.8])
        sphere(4);

        // Eyes
        translate([6, 5, 2])
        eye();
        translate([6, -5, 2])
        mirror([0,1,0])
        eye();

        // Horns
        for(i = [0:1]) {
            translate([2, i ? 4 : -4, 6])
            rotate([i ? -30 : 30, 0, 0])
            cylinder(h=12, r1=2, r2=0.5);
        }

        // Teeth
        for(i = [0:3]) {
            translate([10, 0, -2+i*1.5])
            rotate([0, 90, 0])
            cylinder(h=2, r1=0.5, r2=0);
        }
    }
}

module body_segment(pos, rot, scale) {
    translate(pos)
    rotate([0, 0, rot])
    scale(scale)
    segment(5, 4, 10);
}

module scale_detail(pos, size) {
    translate(pos)
    scale([1, 1, 0.1])
    sphere(size);
}

module body() {
    color("darkslategray")
    union() {
        // Main body segments
        for(i = [0:8]) {
            body_segment(
                [15+i*8, sin(i*15)*3, -i*2],
                i*5,
                [1-i*0.05, 1-i*0.05, 1]
            );
        }

        // Dorsal fins
        for(i = [1:3:8]) {
            fin(15+i*8, sin(i*15)*3, -i*2+5, 0);
        }

        // Scale patterns
        for(i = [0:7]) {
            for(j = [0:5]) {
                scale_detail(
                    [20+i*8, (j-2.5)*2, -i*2-1],
                    1.2
                );
            }
        }
    }
}

module tail() {
    color("darkslategray")
    translate([80, 0, -16])
    rotate([0, 0, 45])
    union() {
        // Tail segments
        for(i = [0:5]) {
            scale([1-i*0.15, 1-i*0.15, 1])
            translate([i*10, 0, 0])
            segment(4-i*0.5, 3-i*0.5, 8);
        }

        // Tail fin
        translate([50, 0, 0])
        rotate([0, 90, 0])
        scale([1, 0.2, 0.5])
        cylinder(h=15, r1=8, r2=0);
    }
}

module magical_glow() {
    color("navy", 0.3)
    translate([0, 0, 0])
    sphere(100);
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor])
union() {
    head();
    body();
    tail();
    magical_glow();
}

