// Infernowyrm - A magma dragon of myth
// Realistic style, medium complexity (~120 lines)

// Global parameters
scale_factor = 0.4;
body_length = 80 * scale_factor;
body_height = 25 * scale_factor;
wing_span = 60 * scale_factor;
horns = 4;
tail_segments = 5;

// Main module
module infernowyrm() {
    // Body
    color("darkred") 
    union() {
        // Main torso
        translate([0, 0, body_height/2])
            scale([1.2, 1, 0.8])
            sphere(d=body_height);

        // Neck
        translate([-body_length/3, 0, body_height*0.8])
            rotate([0, 15, 0])
            cylinder(h=body_length/3, d1=body_height*0.6, d2=body_height*0.8);

        // Head
        translate([-body_length/1.8, 0, body_height*1.2])
            scale([1.3, 0.8, 1])
            sphere(d=body_height*0.8);

        // Snout
        translate([-body_length/1.5, 0, body_height*1.2])
            rotate([0, -10, 0])
            cylinder(h=body_height*0.6, d1=body_height*0.5, d2=body_height*0.3);

        // Horns
        for(i=[0:horns-1]) {
            rotate([0, 0, i*90 + 45])
            translate([-body_length/1.7, body_height*0.3, body_height*1.4])
                rotate([30, 0, 0])
                cylinder(h=body_height*0.5, d1=body_height*0.15, d2=body_height*0.05);
        }

        // Tail
        tail();

        // Wings
        wings();

        // Legs
        legs();

        // Spikes along spine
        spine_spikes();
    }

    // Eyes (glowing)
    color("orange")
    union() {
        translate([-body_length/1.6, -body_height*0.25, body_height*1.3])
            sphere(d=body_height*0.15);
        translate([-body_length/1.6, body_height*0.25, body_height*1.3])
            sphere(d=body_height*0.15);
    }
}

// Tail module with segments
module tail() {
    for(i=[0:tail_segments-1]) {
        translate([body_length/2 + i*body_length/5, 0, body_height/2 - i*body_height/4])
            rotate([0, 0, -i*15])
            scale([1 - i*0.15, 1 - i*0.15, 1 - i*0.1])
            sphere(d=body_height*(0.8 - i*0.1));
    }
}

// Wing module
module wings() {
    color("darkred") {
        // Left wing
        translate([-body_length/4, body_height*0.8, body_height*0.8])
            rotate([0, 0, -30])
            scale([1, wing_span/2, 0.1])
            difference() {
                sphere(d=body_height);
                translate([0, -body_height, 0])
                    cube([body_height*2, body_height*2, body_height], center=true);
            }

        // Right wing
        translate([-body_length/4, -body_height*0.8, body_height*0.8])
            rotate([0, 0, 30])
            scale([1, wing_span/2, 0.1])
            mirror([0, 1, 0])
            difference() {
                sphere(d=body_height);
                translate([0, -body_height, 0])
                    cube([body_height*2, body_height*2, body_height], center=true);
            }
    }
}

// Legs module
module legs() {
    for(side=[-1, 1]) {
        // Front legs
        translate([-body_length/4, side*body_height*0.4, 0])
            rotate([0, 0, side*15])
            cylinder(h=body_height*0.8, d=body_height*0.2);

        // Rear legs
        translate([body_length/4, side*body_height*0.5, 0])
            rotate([0, 0, side*15])
            cylinder(h=body_height*0.9, d=body_height*0.25);
    }
}

// Spine spikes
module spine_spikes() {
    for(i=[-2:2]) {
        if(i != 0) {
            translate([i*body_length/6, 0, body_height*0.8])
                cylinder(h=body_height*0.3, d=body_height*0.1);
        }
    }
}

// Render the creature
infernowyrm();

