use <colors.scad>

module hydra_head(x_pos, y_pos, z_pos, angle) {
    translate([x_pos, y_pos, z_pos]) {
        rotate([0, 0, angle]) {
            // Main head shape
            color("lightblue") {
                sphere(6, $fn=32);
            }

            // Snout
            translate([5, 0, 0]) {
                color("lightblue") {
                    scale([1.5, 0.8, 0.8]) sphere(4, $fn=24);
                }
            }

            // Eyes
            translate([4, -2.5, 2]) {
                color("darkblue") sphere(0.8, $fn=16);
            }
            translate([4, 2.5, 2]) {
                color("darkblue") sphere(0.8, $fn=16);
            }

            // Ice horns
            for(i = [0:45:135]) {
                rotate([0, 0, i]) {
                    translate([0, 5, 4]) {
                        rotate([30, 0, 0]) {
                            color("cyan") {
                                cylinder(h=8, r1=1.5, r2=0.5, $fn=8);
                            }
                        }
                    }
                }
            }

            // Neck connection
            translate([-6, 0, 0]) {
                color("lightblue") {
                    cylinder(h=8, r=4, $fn=16);
                }
            }
        }
    }
}

module hydra_body() {
    // Central body
    color("lightblue") {
        cylinder(h=20, r=8, $fn=32);
    }

    // Ice spikes along back
    for(i = [0:3:20]) {
        translate([0, 8, i]) {
            rotate([45, 0, 0]) {
                color("cyan") {
                    cylinder(h=5, r1=2, r2=0.5, $fn=6);
                }
            }
        }
    }

    // Frost patterns
    for(i = [0:120:359]) {
        rotate([0, 0, i]) {
            translate([6, 0, 5]) {
                color("white") {
                    sphere(0.5, $fn=8);
                }
            }
        }
    }
}

module hydra_legs() {
    // Four legs
    for(side = [-1, 1]) {
        for(pos = [5, 15]) {
            translate([side * 7, 0, pos]) {
                rotate([0, side * 15, 0]) {
                    // Upper leg
                    color("lightblue") {
                        cylinder(h=8, r=2, $fn=12);
                    }

                    translate([0, 0, 8]) {
                        rotate([30, 0, 0]) {
                            // Lower leg
                            color("lightblue") {
                                cylinder(h=6, r=1.5, $fn=12);
                            }

                            // Ice claw foot
                            translate([0, 0, 6]) {
                                color("cyan") {
                                    sphere(2, $fn=16);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

module hydra_tails() {
    // Two splitting tails
    for(side = [-1, 1]) {
        translate([0, side * 5, -10]) {
            rotate([0, side * 10, 0]) {
                color("lightblue") {
                    cylinder(h=15, r=4, $fn=16);
                }

                // Tail spike
                translate([0, 0, -15]) {
                    rotate([0, 0, 0]) {
                        color("cyan") {
                            cylinder(h=8, r1=3, r2=0.5, $fn=8);
                        }
                    }
                }

                // Frost details
                for(i = [0:3:15]) {
                    rotate([0, 0, i * 30]) {
                        translate([3, 0, -i]) {
                            color("white") {
                                sphere(0.8, $fn=8);
                            }
                        }
                    }
                }
            }
        }
    }
}

