    translate([0, 0, 80])
        rotate([0, 0, 0])
            head();

    // Wings
    translate([0, -10, 50])
        rotate([0, 0, 90])
            nebula_wing();
    translate([0, 10, 50])
        rotate([0, 0, -90])
            mirror([1, 0, 0])
