        translate([-2, -5, 8])
            rotate([0, -30, -15])
                cylinder(h=12, r1=2, r2=0.5);
    }
}

module wing_bone() {
    color(bone_color)
    hull() {
        translate([0, 0, 0])
            sphere(d=bone_thickness*2);
        translate([20, 15, -5])
            sphere(d=bone_thickness*1.5);
        translate([30, 5, -10])
            sphere(d=bone_thickness);
