// Color definitions
color_body = "#2F2F2F";
color_bones = "#E8E8E8";
color_eyes = "#FF4500";
color_glow = "#9400D3";

// Main module
module gravewyrm() {
    // Body - segmented serpent-like form
    union() {
        // Head
        translate([0, 0, 15]) {
            gravewyrm_head();
        }

        // Neck
        translate([0, 0, 12]) {
            rotate([10, 0, 0]) {
                cylinder(h=8, r1=6, r2=4, $fn=16);
            }
        }

        // Body segments
        for(i = [0:5]) {
            translate([0, 0, 10 - i * 5]) {
                rotate([i * 5, 0, 0]) {
                    gravewyrm_segment(i);
                }
            }
        }

        // Tail
        translate([0, 0, -25]) {
            gravewyrm_tail();
        }

        // Wings - skeletal membrane style
        translate([0, 8, 8]) {
            rotate([0, -20, 0]) {
                gravewyrm_wing(1);
            }
        }

        translate([0, -8, 8]) {
            rotate([0, -20, 180]) {
                gravewyrm_wing(-1);
            }
        }
    }
}

module gravewyrm_head() {
    color(color_body) {
        difference() {
            // Main head shape
            sphere(8, $fn=32);

            // Eye sockets
            translate([-4, 4, 2]) {
                sphere(3, $fn=16);
            }
            translate([4, 4, 2]) {
                sphere(3, $fn=16);
            }

            // Nostril holes
            translate([-2, 6, 0]) {
                cylinder(h=4, r=1, $fn=8);
            }
            translate([2, 6, 0]) {
                cylinder(h=4, r=1, $fn=8);
            }
        }

        // Eyes
        translate([-4, 4.5, 2]) {
            color(color_eyes) sphere(1.5, $fn=12);
        }
        translate([4, 4.5, 2]) {
            color(color_eyes) sphere(1.5, $fn=12);
        }

        // Horns - bone-like protrusions
        for(i = [-1, 1]) {
            translate([i * 6, 3, 4]) {
                rotate([0, i * 15, 0]) {
                    color(color_bones) cylinder(h=12, r1=2, r2=0.5, $fn=8);
                }
            }
        }

        // Jaw
        translate([0, 3, -3]) {
            rotate([20, 0, 0]) {
                cube([14, 8, 4], center=true);
            }
        }
    }
}

module gravewyrm_segment(index) {
    segment_size = 8 - index * 0.8;
    color(color_body) {
        difference() {
            cylinder(h=6, r=segment_size, $fn=16);

            // Spine ridges
            for(i = [0:3]) {
                rotate([0, 0, i * 90]) {
                    translate([segment_size * 0.8, 0, 7]) {
                        cube([4, 2, 2], center=true);
