// Features: skeletal wings, horned skull, segmented body, glowing eyes
// 3D printable at 50mm length

$fn=32;

// Color definitions
color_doom_black = [0.1, 0.1, 0.1];
color_bone_white = [0.85, 0.8, 0.75];
color_hellfire = [1, 0.2, 0];
color_shadow = [0.2, 0.2, 0.3];

// Main assembly
module doomserpent() {
    union() {
        // Head with skull features
        translate([0, 0, 5]) skull_head();

        // Body segments
        body_segments();

        // Wings - skeletal bat-like
        translate([-5, 0, 8]) rotate([0, 0, -30]) left_wing();
        translate([5, 0, 8]) mirror([1, 0, 0]) rotate([0, 0, -30]) left_wing();

        // Tail
        tail();
    }
}

// Skull head with horns
module skull_head() {
    color(color_bone_white) union() {
        // Main skull
        scale([1.2, 0.8, 1]) sphere(4);

        // Snout
        translate([3, 0, -1]) scale([1.5, 0.7, 0.7]) sphere(2.5);

        // Eye sockets with hellfire
        translate([1.5, 2.2, 1]) {
            color(color_shadow) sphere(1.2);
            color(color_hellfire) translate([0, 0, 0.5]) sphere(0.5);
        }
        translate([1.5, -2.2, 1]) {
            color(color_shadow) sphere(1.2);
            color(color_hellfire) translate([0, 0, 0.5]) sphere(0.5);
        }

        // Curved horns
        for(side = [-1, 1]) {
            translate([0, side * 2.5, 4]) {
                rotate([0, side * 20, 0]) {
                    rotate_extrude(angle = 90) {
                        translate([2, 0, 0]) circle(r=0.8);
                    }
                }
                rotate([0, side * 30, 0]) translate([2, 0, 0]) rotate([90, 0, 0]) cylinder(h=4, r1=1, r2=0.5);
            }
        }
    }
}

// Segmented snake body
module body_segments() {
    color(color_doom_black) {
        segments = 8;
        for(i = [0:segments-1]) {
            translate([-6 - i * 3, 0, 3 - i * 0.5]) {
                scale([1 - i * 0.05, 1 - i * 0.05, 1 - i * 0.05]) {
                    rotate([0, i * 10, 0]) sphere(3.5);
                }
            }
        }
    }
}

// Skeletal wing structure
module left_wing() {
    color(color_bone_white) {
        // Main wing bone
        hull() {
            translate([0, 0, 0]) sphere(1);
            translate([-8, 0, 2]) sphere(0.8);
            translate([-12, -2, 0]) sphere(0.5);
        }

        // Wing fingers
        for(i = [0:3]) {
            translate([-8 - i * 2, 0, 2 - i * 0.5]) {
                rotate([0, 10 - i * 15, 0]) {
                    hull() {
                        sphere(0.5);
                        translate([0, -5 + i, 0]) sphere(0.3);
                    }
                }
            }
        }

        // Wing membrane (thin)
        translate([-10, -3, 1]) rotate([0, 0, 45]) scale([8, 6, 0.5]) cube([1, 1, 0.2], center=true);
    }
}

// Barbed tail
module tail() {
    color(color_doom_black) {
        tail_len = 20;
        for(i = [0:tail_len-1]) {
            t = i / tail_len;
            translate([-30 - i * 1.5, sin(i * 15) * 2, 2 - t * 2]) {
                scale([1 - t * 0.8, 1 - t * 0.8, 1 - t * 0.8]) {
                    rotate([0, i * 8, 0]) sphere(2);
                }
            }

            // Tail spikes
            if(i % 4 == 0 && i > 5) {
                translate([-30 - i * 1.5, sin(i * 15) * 2 + 2, 2 - t * 2]) {
                    rotate([0, 0, 45]) scale([0.5, 0.5, 2]) cylinder(h=2, r1=0.8, r2=0);
                }
            }
        }

        // Tail tip blade
        translate([-60, 0, -2]) rotate([0, 0, 90]) scale([0.5, 0.5, 1]) cylinder(h=4, r1=2, r2=0);
    }
}

