            translate([wing_span/2 - 5, side*wing_span/3, 10])
            rotate([30, 0, side*20])
            cylinder(h = 6, d1 = 3, d2 = 1);
        }

        // Legs
        color("black")
        for(side = [-1, 1]) {
            translate([body_len/4, side*body_len/3, 0])
            rotate([0, 0, side*15])
            cylinder(h = 12, d = 4);
            translate([body_len/4 + 2, side*body_len/3 + side*3, 0])
            rotate([0, 0, side*15])
            cylinder(h = 10, d = 3.5);
        }

        // Horns
        color("dimgray")
        for(i = [0:2]) {
            rotate([0, 0, i*45 - 45])
            translate([-body_len/2-15, 0, 22 + i*2])
            rotate([10, 0, 0])
            cylinder(h = 8 - i*1.5, d1 = 2 + i*0.5, d2 = 1);
        }

        // Tail spikes
        for(i = [0:6]) {
            color("dimgray")
            translate([body_len/2 + 5 + i*4, 0, 8 - i*1.5 + 2])
            rotate([0, 90, 0])
            cylinder(h = 4, d1 = 2, d2 = 0.5);
        }

        // Eye sockets
        color("red")
        translate([-body_len/2-17, 3, 20])
        sphere(d = 2.5);
        translate([-body_len/2-17, -3, 20])
        sphere(d = 2.5);

        // Scale texture on body
        color("darkslategray")
        for(i = [0:3]) {
            for(j = [0:7]) {
                rotate([0, 0, j*45])
                translate([i*7 - body_len/2 + 5, 0, 8 + i*2])
                scale([1, 0.7, 0.3])
                sphere(d = 4);
            }
        }
    }
}

