// Plaguewyrm - A decaying dragon-like creature with pestilent features
// Designed for 3D printing with 1mm wall thickness

// Main colors
$fa = 8;
$fs = 0.5;

module plaguewyrm() {
    // Body - segmented decaying form
    color("darkolivegreen")
    union() {
        // Main torso
        translate([0, 0, 5])
            scale([1.2, 0.8, 1])
            sphere(d=20);

        // Abdomen segments
        for(i = [1:3]) {
            translate([0, 0, 5 - i*8])
                scale([1.1 - i*0.2, 0.7 - i*0.1, 0.8])
                sphere(d=18 - i*2);
        }

        // Tail
        hull() {
            translate([0, 0, -18])
                sphere(d=12);
            translate([0, 0, -35])
                scale([0.3, 0.3, 1])
                sphere(d=8);
        }
    }

    // Head - skull-like with disease features
    color("darkgreen")
    translate([0, 0, 25])
    union() {
        // Main skull shape
        scale([1.3, 1, 1.2])
            sphere(d=15);

        // Snout
        translate([8, 0, 0])
            rotate([0, -20, 0])
            scale([1.5, 0.8, 0.8])
            cylinder(h=12, d1=8, d2=5);

        // Eye sockets - decayed hollows
        color("black") {
            translate([6, 5, 3])
                sphere(d=4);
            translate([6, -5, 3])
                sphere(d=4);
        }

        // Disease pustules
        color("palegreen")
        for(i = [0:7]) {
            rotate([0, 0, i*45])
            translate([4, 8, 0])
                sphere(d=1.5);
        }
    }

    // Horns - diseased growths
    color("darkolivegreen")
    union() {
        // Left horn
        translate([-5, 8, 30])
            rotate([20, 0, -30])
            cylinder(h=15, d1=4, d2=2);

        // Right horn
        translate([-5, -8, 30])
            rotate([20, 0, 30])
            cylinder(h=15, d1=4, d2=2);

        // Back ridge horns
        for(i = [0:4]) {
            translate([0, 0, 20 - i*5])
                rotate([15 + i*5, 0, 0])
                cylinder(h=8 - i, d=3 - i*0.3);
        }
    }

    // Wings - tattered and decaying
    color("darkgreen")
    difference() {
        // Left wing membrane
        translate([-10, 15, 15])
            rotate([0, -20, -30])
            scale([2, 1, 0.1])
            sphere(d=20);

        // Wing tears
        for(i = [0:3]) {
            translate([-15 - i*3, 20 - i*5, 15])
                rotate([0, 0, 30 + i*10])
                scale([0.5, 1, 0.2])
                cube([8, 12, 1]);
        }
    }

    // Right wing
    mirror([0, 1, 0])
    difference() {
        translate([-10, 15, 15])
            rotate([0, -20, -30])
            scale([2, 1, 0.1])
            sphere(d=20);

        for(i = [0:3]) {
            translate([-15 - i*3, 20 - i*5, 15])
                rotate([0, 0, 30 + i*10])
                scale([0.5, 1, 0.2])
                cube([8, 12, 1]);
        }
    }

    // Legs - diseased and twisted
    color("darkolivegreen")
    union() {
        // Front legs
        for(side = [-1, 1]) {
            translate([5, side*8, 10])
                rotate([0, side*10, 0])
                cylinder(h=15, d1=6, d2=4);

            translate([5, side*12, -3])
                rotate([0, side*20, 0])
                cylinder(h=12, d1=4, d2=3);
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([-5, side*8, -5])
                rotate([0, side*15, 0])
                cylinder(h=18, d1=5, d2=4);

            translate([-5, side*12, -18])
                rotate([0, side*25, 0])
                cylinder(h=15, d1=4, d2=3);
        }
    }

    // Claws
    color("black")
    for(side = [-1, 1]) {
        // Front claws
        for(pos = [[5, side*12, -12], [-5, side*12, -25]]) {
            translate(pos)
                for(i = [-1, 0, 1]) {
                    rotate([0, 0, i*30])
                    translate([0, i*2, -3])
                    scale([0.3, 0.3, 1])
                    cylinder(h=4, d=2);
                }
        }
    }

    // Pestilent cloud effect
    color("green", 0.6)
    for(i = [0:5]) {
        rotate([0, 0, i*60])
        translate([0, 12, -15 - i*3])
            scale([1 + i*0.2, 1 + i*0.2, 0.5])
            sphere(d=8 - i);
    }
}

