// Blightserpent - a corrupted dragon-serpent hybrid with decaying wings
// Overall size: ~80mm length, 40mm height, 30mm width

module blightserpent() {
    color("DarkOliveGreen") {
        // Main serpentine body
        union() {
            // Head
            translate([0, 0, 15]) {
                difference() {
                    sphere(d=12);
                    // Eye sockets
                    translate([4, 5, 2]) sphere(d=3);
                    translate([-4, 5, 2]) sphere(d=3);
                }
            }

            // Neck
            translate([0, -8, 12]) rotate([0, 20, 0]) 
                cylinder(h=15, d1=10, d2=8);

            // Body segments
            for(i=[0:4]) {
                translate([0, -15 - i*10, 12 - i*2]) 
                    rotate([0, i*5, 0])
                    cylinder(h=10, d=10 - i);
            }

            // Tail
            translate([0, -55, 5]) rotate([0, -30, 0])
                cylinder(h=25, d1=6, d2=2);
        }

        // Horns - corrupted tree-like growths
        translate([0, 0, 21]) {
            for(angle=[0:120:240]) {
                rotate([0, 0, angle]) {
                    translate([6, 0, 0]) rotate([30, 0, 10])
                        cylinder(h=8, d1=2, d2=1);
                    translate([5, 0, 2]) rotate([45, 0, 5])
                        cylinder(h=6, d1=1.5, d2=0.5);
                }
            }
        }

        // Decaying wings
        color("SaddleBrown") {
            // Left wing
            translate([-5, -10, 18]) rotate([0, 0, -30]) {
                difference() {
                    // Main wing membrane
                    union() {
                        translate([-15, 0, 0]) cube([20, 2, 25]);
                        translate([-15, 0, 0]) rotate([0, 30, 0])
                            cube([15, 2, 20]);
                    }
                    // Holes and decay
                    for(pos=[[-10, 1, 5], [-8, 1, 15], [-12, 1, 10]]) {
                        translate(pos) sphere(d=3);
                    }
                }

                // Wing bones
                color("DarkGoldenrod") {
                    translate([-15, 1, 12]) rotate([0, 45, 0])
                        cylinder(h=18, d=1.5);
                    translate([-15, 1, 8]) rotate([0, 20, 0])
                        cylinder(h=15, d=1);
                }
            }

            // Right wing (mirror of left)
            mirror([1, 0, 0]) {
                translate([-5, -10, 18]) rotate([0, 0, -30]) {
                    difference() {
                        union() {
                            translate([-15, 0, 0]) cube([20, 2, 25]);
                            translate([-15, 0, 0]) rotate([0, 30, 0])
                                cube([15, 2, 20]);
                        }
                        for(pos=[[-10, 1, 5], [-8, 1, 15], [-12, 1, 10]]) {
                            translate(pos) sphere(d=3);
                        }
                    }

                    color("DarkGoldenrod") {
                        translate([-15, 1, 12]) rotate([0, 45, 0])
                            cylinder(h=18, d=1.5);
                        translate([-15, 1, 8]) rotate([0, 20, 0])
                            cylinder(h=15, d=1);
                    }
                }
            }
        }

        // Legs
        color("DarkGreen") {
            for(side=[-1, 1]) {
                translate([side*4, -15, 8]) {
                    // Upper leg
                    rotate([side*30, 0, 0]) 
                        cylinder(h=8, d=3);
                    // Lower leg
                    translate([0, 0, -8]) rotate([side*60, 0, 0])
                        cylinder(h=6, d=2);
                    // Foot
                    translate([0, -3, -12]) 
                        cube([3, 6, 1]);
                }

                // Rear legs
                translate([side*3, -35, 5]) {
                    rotate([side*30, 0, 0]) 
                        cylinder(h=7, d=2.5);
                    translate([0, 0, -7]) rotate([side*50, 0, 0])
                        cylinder(h=5, d=2);
                    translate([0, -2, -10]) 
                        cube([2.5, 5, 1]);
                }
            }
        }

        // Scales pattern
        color("OliveDrab") {
            for(i=[0:3]) {
                for(j=[0:8]) {
                    translate([sin(j*30)*3, -20 - j*5, 10 - j + sin(i*45)*1])
                        sphere(d=1.5);
                }
            }
        }

        // Eyes - glowing corruption
        color("DarkRed") {
            translate([4, 5, 2]) sphere(d=1.5);
            translate([-4, 5, 2]) sphere(d=1.5);
        }
    }
}

