// Ashwyrm - a wingless, serpentine dragon forged from volcanic ash
// Realistic medium-complexity OpenSCAD model

// Parameters
$fn = 36;
scale_factor = 1;             // overall scale
body_len = 60;                // mm
head_len = 15;
tail_len = 40;
segment_count = 12;
shell_thick = 1.5;            // for printability

// Color palette
ash_dark = [0.15, 0.15, 0.15];
ash_light = [0.35, 0.35, 0.35];
ember_orange = [0.9, 0.35, 0.05];

// Main assembly
color(ash_dark)
difference() {
    union() {
        head();
        body();
        tail();
    }
    // Hollow cavity for printing
    translate([0, 0, -1])
        scale([0.7, 0.7, 0.9])
        union() {
            head();
            body();
            tail();
        }
}

// Head module
module head() {
    translate([body_len/2 + head_len/2, 0, 0])
    rotate([0, 90, 0])
    union() {
        // snout
        cylinder(h = head_len, r1 = 6, r2 = 4);
        // cranium
        translate([0,0,head_len])
            sphere(d = 10);
        // horns
        for(a=[-30,30])
            rotate([a,0,0])
            translate([0,0,12])
                cylinder(h = 8, r1 = 1.5, r2 = 0.5);
        // glowing eye sockets
        color(ember_orange)
        translate([3,3,head_len-3])
            sphere(d = 3);
        color(ember_orange)
        translate([3,-3,head_len-3])
            sphere(d = 3);
    }
}

// Body module with segments
module body() {
    for(i=[0:segment_count-1]) {
        translate([body_len/2 - i*(body_len/segment_count), 0, 0])
        rotate([0, 0, i*5])
        scale([1 - 0.03*i, 1 - 0.03*i, 1 - 0.03*i])
        union() {
            // dorsal ridge
            translate([0,0,5])
                cube([body_len/segment_count + 0.5, 2, 1.5], center = true);
            // segment ring
            rotate_extrude()
                translate([4,0,0])
                    circle(r = 1.5);
            // scale pattern
            for(j=[0:3])
                rotate([0,0,j*90])
                translate([body_len/segment_count/4, 3.5, 0])
                    scale([0.5,0.5,0.5])
                    sphere(d = 2);
        }
    }
}

// Tail module
module tail() {
    translate([-body_len/2 - tail_len/2, 0, 0])
    rotate([0, 90, 0])
    hull() {
        cylinder(h = 5, r = 4);
        translate([0,0,tail_len])
            cylinder(h = 1, r = 1);
    }
    // tail spikes
    for(i=[1:8]) {
        rotate([0,0,i*45])
        translate([0,0,i*tail_len/8])
        cube([0.5, 6, 1.5], center = true);
    }
}

