// Stonecoil - a mythical serpent made of living stone with crystal core
// Realistic style, medium complexity
// 3D printable, 80mm length

// Main parameters
body_length = 80;
body_segments = 12;
segment_diameter = 12;
crystal_size = 6;

// Stone color
stone_color = [0.5, 0.45, 0.4];

// Create one segment of the serpent body
module body_segment(diameter, length, twist) {
    union() {
        // Main stone segment
        difference() {
            cylinder(h = length, d = diameter, $fn = 8);
            // Carve runes into the stone
            for(i = [0:3]) {
                rotate([0, 0, i * 90])
                    translate([diameter/2 - 1, 0, length/2])
                        cube([2, 1, 1], center = true);
            }
        }

        // Crystal core visible through cracks
        color([0.7, 0.6, 0.9])
        translate([0, 0, length/2])
            rotate([0, 0, twist])
                sphere(d = crystal_size * 0.6, $fn = 6);
    }
}

// Create stone scales around the segment
module scales(diameter, height) {
    for(i = [0:7]) {
        angle = i * 45;
        color(stone_color)
        rotate([0, 0, angle])
            translate([diameter/2 * 0.8, 0, height/2])
                scale([0.3, 0.4, 0.2])
                    sphere(d = diameter/3, $fn = 5);
    }
}

// Create the head with horns and eyes
module head() {
    union() {
        // Main head
        color(stone_color)
        scale([1.2, 1, 1])
            sphere(d = segment_diameter * 1.3, $fn = 8);

        // Horns
        for(side = [-1, 1]) {
            color([0.4, 0.35, 0.3])
            translate([side * segment_diameter/2, 0, segment_diameter/2])
                rotate([0, side * 20, 0])
                    cylinder(h = segment_diameter/2, d1 = segment_diameter/3, d2 = segment_diameter/6, $fn = 6);
        }

        // Crystal eyes
        for(side = [-1, 1]) {
            color([0.8, 0.7, 1])
            translate([side * segment_diameter/3, segment_diameter/2 * 0.7, 0])
                sphere(d = segment_diameter/8, $fn = 6);
        }

        // Fangs
        color([0.6, 0.6, 0.6])
        translate([0, segment_diameter/2 * 0.8, -segment_diameter/3])
            rotate([90, 0, 0])
                cylinder(h = segment_diameter/4, d1 = segment_diameter/10, d2 = segment_diameter/20, $fn = 4);
    }
}

// Create the full stonecoil
module stonecoil() {
    // Head
    translate([0, 0, body_length/2 + segment_diameter/2])
        head();

    // Body segments
    for(i = [1:body_segments]) {
        segment_height = body_length / body_segments;
        segment_twist = i * 15;

        // Position each segment in a coiling pattern
        angle = i * 30;
        radius = body_length/4 * (1 - i/body_segments);

        translate([
            sin(angle) * radius,
            cos(angle) * radius,
            body_length/2 - i * segment_height
        ]) {
            rotate([0, 0, angle + 90])
                body_segment(segment_diameter * (1 - i/body_segments * 0.3), segment_height, segment_twist);

            // Add scales to each segment except last few
            if(i < body_segments - 2)
                scales(segment_diameter * (1 - i/body_segments * 0.3), segment_height);
        }
    }

    // Tail tip with crystal
    translate([0, 0, -body_length/2 - segment_diameter/4])
        color([0.6, 0.5, 0.45])
        sphere(d = segment_diameter/2, $fn = 8);
}

// Render the creature
stonecoil();

