                rotate([0, 0, side*(20-i*15)])
                cylinder(h=25-i*5, r=1.5);
        }
    }

    // Tail
    color(color_ice)
    translate([25, 0, 15])
    union() {
        for(i = [0:5]) {
            translate([i*6, 0, max(0, -i*3)])
                scale([1-i*0.15, 1-i*0.15, 1-i*0.1])
                sphere(d=15-i*2);
