// Reedserpent - A mythical serpentine creature with reed-like appendages
// Wings included for gliding over marshes, reed fronds for camouflage

module reedserpent() {
    color([0.2, 0.4, 0.2]) // Deep green body

    union() {
        // Main serpentine body
        hull() {
            translate([0, 0, 5]) sphere(d=8, $fn=20);
            translate([0, 0, 15]) sphere(d=12, $fn=20);
            translate([0, 0, 25]) sphere(d=14, $fn=20);
            translate([0, 0, 35]) sphere(d=12, $fn=20);
            translate([0, 0, 45]) sphere(d=8, $fn=20);
        }

        // Head
        translate([0, 0, 50]) {
            color([0.15, 0.35, 0.15])
            sphere(d=10, $fn=20);

            // Eyes
            color([0.8, 0.8, 0.2])
            translate([2.5, 3, 2]) sphere(d=1.5, $fn=10);
            translate([-2.5, 3, 2]) sphere(d=1.5, $fn=10);

            // Snout ridges
            color([0.1, 0.3, 0.1])
            for(i=[-3:3]) {
                translate([i*0.8, 0, 5-i]) cylinder(h=0.5, d1=1, d2=0.3, $fn=6);
            }
        }

        // Reed fronds along back
        color([0.3, 0.5, 0.2])
        for(pos=[8:8:40]) {
            translate([0, 8, pos]) rotate([30, 0, 0]) 
                cylinder(h=12, d1=2, d2=0.5, $fn=6);
            translate([0, -8, pos]) rotate([-30, 0, 0]) 
                cylinder(h=12, d1=2, d2=0.5, $fn=6);
        }

        // Wings - membrane between reed structures
        color([0.25, 0.45, 0.25, 0.7])
        translate([0, 8, 25]) rotate([0, 0, 45]) {
            // Wing frame
            for(i=[0:15:60]) {
                rotate([0, 0, i]) 
                    translate([0, 0, 0]) cylinder(h=20, d1=1, d2=0.5, $fn=4);
            }
            // Wing membrane
            hull() {
                translate([0, 0, 0]) sphere(d=1);
                translate([15, 0, 0]) sphere(d=1);
                translate([10, 0, 10]) sphere(d=1);
            }
        }

        mirror([0, 1, 0]) translate([0, 8, 25]) rotate([0, 0, 45]) {
            for(i=[0:15:60]) {
                rotate([0, 0, i]) 
                    translate([0, 0, 0]) cylinder(h=20, d1=1, d2=0.5, $fn=4);
            }
            hull() {
                translate([0, 0, 0]) sphere(d=1);
                translate([15, 0, 0]) sphere(d=1);
                translate([10, 0, 10]) sphere(d=1);
            }
        }

        // Tail segments
        color([0.2, 0.4, 0.2])
        for(i=[1:5]) {
            translate([0, 0, -i*6]) scale([1-i*0.1, 1-i*0.1, 1])
                sphere(d=8-i, $fn=16);
        }

        // Reed-like fins
        color([0.35, 0.55, 0.25])
        for(i=[10:10:30]) {
            rotate([0, 0, i]) translate([7, 0, 25]) 
                rotate([90, 0, 0]) cylinder(h=6, d1=2, d2=1, $fn=6);
            rotate([0, 0, -i]) translate([-7, 0, 25]) 
                rotate([90, 0, 0]) cylinder(h=6, d1=2, d2=1, $fn=6);
        }

        // Magical glow spots
        color([0.4, 0.6, 0.3, 0.5])
        for(pos=[12:8:36]) {
            translate([5, 3, pos]) sphere(d=1.5, $fn=8);
            translate([-5, -3, pos]) sphere(d=1.5, $fn=8);
        }
    }
}

