// Base plate for stability
color("darkslategray")
    translate([0, 0, -10])
        cube([80, 30, 1], center = true);

// Coralcoil - Mythical sea serpent with coral-like features
// Features: coiled body, coral growths, bioluminescent accents, dragon-like head

module coralcoil() {
    // Main body coil
    color("darkslateblue")
    union() {
        // Body segments in spiral
        for (i = [0:15:360]) {
            rotate([0, 0, i]) 
            translate([25 * cos(i/2), 25 * sin(i/2), i/2])
            rotate([90, 0, 0])
            scale([1.5 - i/360, 1.5 - i/360, 1])
            cylinder(h=15, r1=8, r2=6, $fn=12);
        }

        // Head section
        translate([0, 0, 180])
        union() {
            color("darkslateblue")
            scale([1.2, 1.2, 1.5])
            sphere(12, $fn=16);

            // Snout
            color("steelblue")
            translate([0, 0, 15])
            scale([0.8, 0.6, 1.5])
            sphere(8, $fn=12);

            // Horns
            color("darkslategray")
            for (h = [-1, 1]) {
                translate([h*8, 0, 20])
                rotate([0, h*30, 0])
                cylinder(h=15, r1=3, r2=1, $fn=8);
            }

            // Eyes
            color("cyan")
            for (e = [-1, 1]) {
                translate([e*5, -10, 5])
                sphere(2, $fn=8);
            }
        }
    }

    // Coral growths along body
    color("coral")
    for (i = [0:30:360]) {
        rotate([0, 0, i]) 
        translate([25 * cos(i/2) + 10, 25 * sin(i/2), i/2])
        scale([0.3, 0.3, 1])
        cylinder(h=8 + 5 * sin(i), r1=3, r2=1, $fn=6);
    }

    // Fins along back
    color("steelblue")
    for (f = [60:30:300]) {
        rotate([0, 0, f])
        translate([25 * cos(f/2), 25 * sin(f/2), f/2 + 5])
        rotate([0, 90, 0])
        scale([0.5, 0.8, 0.1])
        cylinder(h=15, r1=6, r2=3, $fn=4);
    }

    // Bioluminescent spots
    color("aquamarine")
    for (b = [0:45:360]) {
        rotate([0, 0, b])
        translate([25 * cos(b/2), 25 * sin(b/2), b/2 + 5])
        sphere(1.5, $fn=8);
    }

    // Tail end
    color("darkslateblue")
    translate([0, 0, 0])
    rotate([0, 0, 0])
    scale([0.8, 0.8, 2])
    cylinder(h=20, r1=6, r2=2, $fn=8);

    // Underside scales
    color("steelblue")
    for (s = [0:20:360]) {
        rotate([0, 0, s])
        for (r = [0:1:3]) {
            translate([r*5 + 10, 0, s/2 - 2])
            scale([0.8, 1, 0.3])
            sphere(2, $fn=6);
        }
    }
}

// Base for stability
color("slategray")
translate([0, 0, -5])
cylinder(h=5, r=35, $fn=32);

// Place the coralcoil
translate([0, 0, 5])
rotate([0, 0, 45])
coralcoil();

// Krakenwyrm - a mythical sea-dragon with cephalopod features
// 3D printable, medium complexity, realistic style

$fn = 32;

// Main body
module krakenwyrm_body() {
    // Torso - elongated sea serpent body
    union() {
        color("darkslategray")
        translate([0, 0, -5])
        scale([1.2, 0.8, 1.5])
        sphere(15);

        // Neck extension
        color("midnightblue")
        translate([0, 0, 10])
        scale([0.6, 0.4, 1])
        cylinder(h=20, r1=8, r2=5);
    }
}

// Dragon head with cephalopod features
module krakenwyrm_head() {
    union() {
        // Main head shape
        color("steelblue")
        translate([0, 0, 35])
        scale([1, 0.7, 0.8])
        sphere(12);

        // Snout
        color("darkblue")
        translate([0, 0, 42])
        scale([0.6, 0.4, 0.6])
        cylinder(h=10, r1=5, r2=0);

        // Eyes
        for(side = [-1, 1]) {
            color("gold")
            translate([side * 5, 8, 38])
            sphere(2.5);

            color("black")
            translate([side * 5.5, 9, 39])
            sphere(1);
        }

        // Tentacle beard
        for(i = [0:3]) {
            color("slateblue")
            rotate([0, 0, i * 45 - 67.5])
            translate([0, -10, 32])
            rotate([90, 0, 0])
            scale([0.3, 0.3, 1])
            cylinder(h=15, r=2);
        }
    }
}

// Wings - fin-like dragon wings
module krakenwyrm_wings() {
    for(side = [-1, 1]) {
        color("darkslateblue")
        translate([side * 15, 0, 15])
        rotate([0, side * 20, 0])
        scale([1, 0.2, 0.8])
        sphere(20);

        // Wing membranes
        for(i = [1:3]) {
            color("royalblue")
            translate([side * (15 + i * 5), 0, 15 - i * 3])
            scale([2, 0.1, 1])
            rotate([0, side * 30, 0])
            sphere(5);
        }
    }
}

// Tentacles instead of legs
module krakenwyrm_tentacles() {
    for(i = [0:7]) {
        angle = i * 45;
        color("indigo")
        rotate([0, 0, angle])
        translate([12, 0, -10])
        rotate([90, 0, 0])
        scale([0.8, 0.8, 1])
        cylinder(h=25, r=3);

        // Suction cups
        for(j = [1:4]) {
            color("darkorchid")
            rotate([0, 0, angle])
            translate([12 + j * 2, 0, -10 - j * 4])
            sphere(1.5);
        }
    }
}

// Tail - serpentine with fins
module krakenwyrm_tail() {
    color("darkslategray")
    translate([0, 0, -20])
    rotate([0, 15, 0])
    scale([0.8, 0.6, 1.2])
    sphere(10);

    // Tail fins
    color("blue")
    translate([0, 0, -30])
    rotate([0, 30, 0])
    scale([2, 0.2, 1])
    sphere(8);

    color("blue")
    translate([0, 0, -30])
    rotate([0, -30, 180])
    scale([2, 0.2, 1])
    sphere(8);
}

// Dorsal spines
module krakenwyrm_spines() {
    for(i = [-2:2]) {
        color("darkslateblue")
        translate([0, 0, i * 5 + 5])
        rotate([0, 0, 0])
        scale([0.3, 0.3, 1])
        cylinder(h=8, r=2);
    }
}

// Main assembly
union() {
    krakenwyrm_body();
    krakenwyrm_head();
    krakenwyrm_wings();
    krakenwyrm_tentacles();
    krakenwyrm_tail();
    krakenwyrm_spines();
}

// Unicorn - Realistic, Medium Complexity
// Approx 80mm long, 60mm tall

// Main module
unicorn();

module unicorn() {
    color("white") {
        // Body
        union() {
            // Main torso
            translate([0, 0, 20])
                scale([1.5, 0.8, 1])
                sphere(d=30, $fn=32);

            // Neck
            translate([20, 0, 35])
                rotate([0, -30, 0])
                scale([0.6, 0.6, 1.2])
                cylinder(h=25, d1=15, d2=12, $fn=16);

            // Head
            translate([40, 0, 45])
                rotate([0, -30, 0])
                scale([1.2, 0.8, 0.8])
                sphere(d=20, $fn=32);

            // Legs
            for(i = [-1, 1]) {
                // Front legs
                translate([15, i*8, 10])
                    rotate([0, 5*i, 0])
                    cylinder(h=30, d=6, $fn=12);

                // Hind legs
                translate([-15, i*8, 10])
                    rotate([0, -5*i, 0])
                    cylinder(h=32, d=7, $fn=12);
            }

            // Tail
            translate([-25, 0, 25])
                rotate([0, 0, 15])
                scale([0.3, 0.3, 1])
                cylinder(h=40, d=12, $fn=16);
        }

        // Horn
        color("gold") {
            translate([50, 0, 55])
                rotate([0, 90, 0])
                cylinder(h=25, d1=2, d2=0.5, $fn=8);
        }

        // Mane
        color("lightblue") {
            for(i = [0:3]) {
                translate([35 - i*5, 5, 45 - i*3])
                    rotate([0, -30 - i*5, 0])
                    scale([0.2, 0.5, 1])
                    sphere(d=8, $fn=8);
            }
        }

        // Tail hair
        color("lightblue") {
            for(i = [0:5]) {
                translate([-25 - i*2, 5 + i*2, 25 - i*3])
                    rotate([0, 0, 20 + i*10])
                    scale([0.3, 0.3, 1])
                    cylinder(h=15, d=4, $fn=6);
            }
        }

        // Hooves
        color("gray") {
            for(i = [-1, 1]) {
                translate([15, i*8, 0])
                    cylinder(h=3, d=7, $fn=12);
                translate([-15, i*8, 0])
                    cylinder(h=3, d=8, $fn=12);
            }
        }

        // Eyes
        color("black") {
            translate([45, 6, 48])
                sphere(d=2, $fn=8);
            translate([45, -6, 48])
                sphere(d=2, $fn=8);
        }

        // Ears
        color("white") {
            translate([42, 5, 58])
                rotate([0, 0, -30])
                scale([0.3, 0.1, 0.5])
                cylinder(h=10, d=8, $fn=6);
            translate([42, -5, 58])
                rotate([0, 0, 30])
                scale([0.3, 0.1, 0.5])
                cylinder(h=10, d=8, $fn=6);
        }

        // Wings (folded)
        color("white") {
            translate([0, 10, 35])
                rotate([0, 0, -20])
                scale([1, 0.1, 0.8])
                sphere(d=25, $fn=16);
            translate([0, -10, 35])
                rotate([0, 0, 20])
                scale([1, 0.1, 0.8])
                sphere(d=25, $fn=16);
        }

        // Magical sparkles on body
        color("lightcyan") {
            for(i = [0:10]) {
                theta = i * 36;
                phi = i * 25;
                x = 15 * cos(theta);
                y = 8 * sin(theta);
                z = 25 + 5 * sin(phi);
                translate([x, y, z])
                    sphere(d=1, $fn=4);
            }
        }
    }
}

use <colors.scad>

// Griffin - medium complexity realistic style
// Size: 70mm wingspan, 50mm length

$fa = 2;
$fs = 1;

module griffin() {
    color("goldenrod") {
        // Body
        union() {
            // Main torso
            scale([1.2, 0.8, 1]) sphere(8);

            // Lion chest
            translate([0, -5, -3]) scale([1.5, 1, 0.8]) sphere(6);

            // Back ridge
            for(i = [0:3]) {
                translate([-6 + i*4, 0, 7]) rotate([0, 0, i*5]) cube([2, 2, 1]);
            }
        }

        // Eagle head
        translate([12, 0, 5]) {
            // Beak
            color("darkorange") {
                translate([3, 0, -2]) rotate([0, -20, 0]) 
                    scale([1.5, 0.5, 0.5]) cylinder(h=8, r1=3, r2=0);
            }

            // Head sphere
            scale([1, 0.8, 1]) sphere(6);

            // Eyes
            color("black") {
                translate([2, -2.5, 1]) sphere(1);
                translate([2, 2.5, 1]) sphere(1);
            }

            // Ear tufts
            color("goldenrod") {
                translate([0, -3, 5]) rotate([30, 0, -30]) 
                    scale([0.3, 0.3, 1]) cylinder(h=3, r1=2, r2=0);
                translate([0, 3, 5]) rotate([30, 0, 30]) 
                    scale([0.3, 0.3, 1]) cylinder(h=3, r1=2, r2=0);
            }
        }

        // Wings
        color("burlywood") {
            // Left wing
            translate([-5, -12, 8]) rotate([0, 0, -20]) {
                // Main wing
                scale([1, 0.2, 2]) sphere([15, 5, 3]);

                // Wing feathers
                for(i = [0:4]) {
                    translate([-5 - i*3, 0, -8 + i*2]) 
                        rotate([0, -10 - i*5, 0])
                        scale([1, 0.1, 0.5]) 
                        cube([8, 1, 2]);
                }
            }

            // Right wing
            translate([-5, 12, 8]) mirror([0, 1, 0]) {
                // Main wing
                scale([1, 0.2, 2]) sphere([15, 5, 3]);

                // Wing feathers
                for(i = [0:4]) {
                    translate([-5 - i*3, 0, -8 + i*2]) 
                        rotate([0, -10 - i*5, 0])
                        scale([1, 0.1, 0.5]) 
                        cube([8, 1, 2]);
                }
            }
        }

        // Legs
        color("goldenrod") {
            // Front legs
            for(side = [-1, 1]) {
                translate([5, side*5, -6]) {
                    // Upper leg
                    rotate([15, 0, side*10]) 
                        cylinder(h=8, r=2);

                    // Lower leg
                    translate([0, 0, -9]) rotate([30, 0, 0]) 
                        cylinder(h=6, r=1.5);

                    // Paw
                    translate([0, 0, -16]) 
                        scale([1.2, 1, 0.8]) sphere(2);

                    // Talons
                    color("darkorange") {
                        for(t = [-30, 0, 30]) {
                            rotate([0, 0, t]) 
                                translate([0, 1.5, -18]) 
                                cylinder(h=2, r1=0.5, r2=0.2);
                        }
                    }
                }
            }
        }

        // Tail
        translate([-12, 0, 0]) {
            // Lion tail
            color("goldenrod") {
                rotate([0, 10, 0]) 
                    cylinder(h=15, r1=2, r2=1);
            }

            // Eagle feathers
            color("burlywood") {
                for(i = [0:5]) {
                    translate([-12 - i*1.5, 0, 1 + i*0.5]) 
                        rotate([0, 0, i*15]) 
                        scale([0.5, 0.05, 1]) 
                        cube([3, 0.5, 1.5]);
                }
            }
        }
    }
}

griffin();

// Pegasus - Winged Horse from Greek Mythology
// Medium complexity realistic style with 3D printability

$fn = 32;

// Overall scaling for 3D printing
scale_factor = 0.7;

// Colors
pegasus_body = [0.9, 0.85, 0.8];  // White alabaster
mane_color = [0.7, 0.6, 0.5];    // Light gray
wing_color = [0.95, 0.95, 0.95]; // Pure white
hoof_color = [0.3, 0.3, 0.3];    // Dark gray

module pegasus() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Body - main torso
            color(pegasus_body)
            translate([0, 0, 20])
            scale([1.5, 1, 1.2])
            sphere(15);

            // Neck
            color(pegasus_body)
            translate([10, 0, 35])
            rotate([0, 15, 0])
            cylinder(h=20, r1=8, r2=6);

            // Head
            color(pegasus_body)
            translate([25, 0, 45])
            rotate([0, 15, 0])
            scale([1.2, 0.8, 0.9])
            sphere(8);

            // Snout
            color(pegasus_body)
            translate([30, 0, 43])
            rotate([0, 15, 0])
            scale([1.5, 0.7, 0.7])
            cylinder(h=8, r1=5, r2=3);

            // Ears
            for (ear_x = [-1, 1]) {
                color(pegasus_body)
                translate([22, ear_x * 4, 52])
                rotate([0, 0, ear_x * 15])
                scale([0.5, 0.2, 1])
                cylinder(h=6, r1=3, r2=1);
            }

            // Mane
            module mane_strand(i) {
                color(mane_color)
                translate([20 - i * 2, 0, 45 - i])
                rotate([0, 10, i * 5])
                scale([0.3, 0.3, 1])
                cylinder(h=8, r1=1, r2=0.5);
            }

            for (i = [0:8]) {
                mane_strand(i);
            }

            // Legs
            module leg(position, angle) {
                color(pegasus_body)
                translate(position)
                rotate([angle, 0, 0])
                cylinder(h=25, r1=4, r2=3);

                // Hoof
                color(hoof_color)
                translate([0, 0, 25])
                cylinder(h=3, r1=3, r2=3.5);
            }

            // Front legs
            leg([-5, 8, 20], 20);
            leg([-5, -8, 20], 20);

            // Back legs
            leg([-20, 8, 20], -20);
            leg([-20, -8, 20], -20);

            // Tail
            color(mane_color)
            translate([-22, 0, 22])
            rotate([0, -30, 0])
            scale([0.5, 0.5, 1])
            cylinder(h=20, r1=4, r2=2);

            // Wings
            module wing(angle) {
                color(wing_color)
                translate([0, angle * 15, 35])
                rotate([0, 0, angle * 30])
                rotate([10, 0, 0])
                scale([1, 0.3, 0.1])
                sphere(18);

                // Wing feathers
                for (i = [0:4]) {
                    color(wing_color)
                    translate([i * 3 - 5, angle * (15 + i * 2), 35 - i])
                    rotate([0, 0, angle * (30 - i * 5)])
                    rotate([10 + i * 5, 0, 0])
                    scale([0.8, 0.2, 0.05])
                    sphere(15 - i * 2);
                }
            }

            // Left wing
            wing(1);

            // Right wing
            wing(-1);

            // Eye
            color([0, 0, 0])
            translate([28, 3, 48])
            sphere(1.5);

            // Nostrils
            color([0.5, 0.4, 0.4])
            translate([33, 1.5, 42])
            sphere(1);
            translate([33, -1.5, 42])
            sphere(1);

            // Chest detail
            color([0.85, 0.8, 0.75])
            translate([5, 0, 25])
            scale([1.2, 0.8, 0.8])
            sphere(12);
        }
    }
}

pegasus();

// Centaur - Realistic Style, Medium Complexity
// Designed for 3D printing - approximately 80mm tall

// Color definitions
color_human = "Burlywood";
color_horse = "SaddleBrown";
color_hair = "SaddleBrown";
color_hooves = "DimGray";

// Overall scale factor
scale_factor = 0.8;

module centaur() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Human torso
            color(color_human) {
                // Chest
                translate([0, 0, 45]) {
                    scale([1.5, 1, 2]) {
                        sphere(8, $fn=32);
                    }
                }

                // Abdomen
                translate([0, 0, 38]) {
                    cylinder(h=10, r1=7, r2=6, $fn=32);
                }

                // Neck
                translate([0, 0, 52]) {
                    cylinder(h=8, r=3, $fn=16);
                }

                // Head
                translate([0, 0, 62]) {
                    scale([1.2, 1, 1.4]) {
                        sphere(7, $fn=32);
                    }
                }

                // Arms
                // Left arm
                translate([-8, 0, 48]) {
                    rotate([0, 0, 20]) {
                        cylinder(h=15, r=2.5, $fn=16);
                    }
                }

                // Right arm
                translate([8, 0, 48]) {
                    rotate([0, 0, -20]) {
                        cylinder(h=15, r=2.5, $fn=16);
                    }
                }

                // Hands
                translate([-11, 0, 48]) {
                    sphere(2.5, $fn=16);
                }

                translate([11, 0, 48]) {
                    sphere(2.5, $fn=16);
                }
            }

            // Horse body
            color(color_horse) {
                // Main body
                translate([0, 0, 25]) {
                    scale([2, 1.2, 1.5]) {
                        sphere(12, $fn=32);
                    }
                }

                // Rear section
                translate([0, -15, 22]) {
                    scale([1.8, 2, 1.4]) {
                        sphere(10, $fn=32);
                    }
                }

                // Front horse shoulders
                translate([0, 12, 22]) {
                    scale([2, 1.5, 1.3]) {
                        sphere(8, $fn=32);
                    }
                }
            }

            // Legs
            color(color_horse) {
                // Front legs
                for (x = [-6, 6]) {
                    translate([x, 10, 15]) {
                        cylinder(h=15, r=3, $fn=16);
                    }

                    // Front hooves
                    color(color_hooves) {
                        translate([x, 10, 0]) {
                            cylinder(h=3, r=3.5, r1=3.5, r2=2.5, $fn=16);
                        }
                    }
                }

                // Rear legs
                for (x = [-5, 5]) {
                    translate([x, -15, 15]) {
                        rotate([5, 0, x > 0 ? -5 : 5]) {
                            cylinder(h=15, r=3.5, $fn=16);
                        }
                    }

                    // Rear hooves
                    color(color_hooves) {
                        translate([x, -15, 0]) {
                            cylinder(h=3, r=4, r1=4, r2=3, $fn=16);
                        }
                    }
                }
            }

            // Tail
            color(color_hair) {
                translate([0, -25, 20]) {
                    rotate([10, 0, 0]) {
                        for (i = [0:5]) {
                            rotate([0, 0, i * 15]) {
                                translate([0, -i * 1.5, -i * 0.5]) {
                                    scale([0.3, 1, 0.3]) {
                                        sphere(3, $fn=8);
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // Hair
            color(color_hair) {
                translate([0, -2, 65]) {
                    for (angle = [0:30:330]) {
                        rotate([0, 0, angle]) {
                            translate([0, 6, 0]) {
                                scale([0.5, 1, 0.1]) {
                                    sphere(3, $fn=8);
                                }
                            }
                        }
                    }
                }
            }

            // Ears
            color(color_human) {
                translate([-3, 0, 66]) {
                    rotate([0, -15, 0]) {
                        scale([0.5, 0.2, 1]) {
                            cylinder(h=3, r1=2, r2=0.5, $fn=8);
                        }
                    }
                }

                translate([3, 0, 66]) {
                    rotate([0, 15, 0]) {
                        scale([0.5, 0.2, 1]) {
                            cylinder(h=3, r1=2, r2=0.5, $fn=8);
                        }
                    }
                }
            }

            // Eyes
            translate([-2.5, -5, 66]) {
                sphere(1, $fn=16);
            }

            translate([2.5, -5, 66]) {
                sphere(1, $fn=16);
            }

            // Nose bridge
            color(color_human) {
                translate([0, -7, 63]) {
                    scale([0.7, 0.5, 0.3]) {
                        sphere(2, $fn=16);
                    }
                }
            }
        }
    }
}

