        rotate([0, 20, 0])
        scale([1.5, 1, 1])
        sphere(3, $fn=16);
    }
}

// Kraken eyes
module kraken_eyes() {
    color(eye_color)
    union() {
        translate([-5, 9, 22])
        sphere(2.5, $fn=16);

        translate([5, 9, 22])
        sphere(2.5, $fn=16);
    }

    color("black")
    union() {
        translate([-5, 10, 23])
        sphere(1, $fn=8);

        translate([5, 10, 23])
        sphere(1, $fn=8);
    }
}

// Kraken beak
module kraken_beak() {
    color(beak_color)
    union() {
        // Upper beak
        translate([0, -5, 10])
        rotate([30, 0, 0])
        cylinder(h=12, r1=8, r2=4, $fn=16);

