        rotate([30, 0, 0])
        cylinder(h=10, r1=6, r2=3, $fn=16);
    }
}

// Single tentacle
module tentacle(length, thickness, curl) {
    linear_extrude(height=length, twist=curl, scale=[1, 0.3], $fn=8)
    circle(thickness, $fn=16);
}

// Generate curled tentacles
module kraken_tentacles() {
    color(kraken_color)
    for(i = [0:7]) {
        angle = i * 45;
        curl = 30 + i * 10;
        length = 25 + (i % 2) * 10;

        translate([0, 0, 5])
        rotate([0, 0, angle])
        translate([15, 0, 0])
        rotate([90, 0, 0])
        tentacle(length, 3 + (i % 3), curl);
    }
}

// Suction cups on tentacles
module suction_cups() {
    color("DimGray")
    for(i = [0:7]) {
        angle = i * 45;
        for(j = [1:4]) {
            translate([0, 0, 5])
            rotate([0, 0, angle])
            translate([15 + j * 6, 0, j * 5])
            rotate([90, 0, 0])
            sphere(1.2, $fn=8);
        }
    }
}

