        // Arms for each torso
        arm(i * 20, -1);
        arm(i * 20, 1);
    }

    // Shared legs
    leg(-10, 0);
    leg(10, 0);
    leg(0, 0);

    // Wings
    wings();

    // Tail
    tail();
}
