            rotate([0, -20, 0])
            scale([0.4, 0.1, 1.2])
            cylinder(h=20, r1=5, r2=2, $fn=8);
    }

    // Antlers - deer-like branching
    color("SaddleBrown") {
        // Left antler
        translate([-15, -12, 35]) {
            // Main branch
            rotate([0, -30, 0])
                cylinder(h=18, r1=2, r2=1, $fn=8);

            // Branch 1
            translate([0, 0, 12])
                rotate([0, 10, -30])
                cylinder(h=10, r1=1.5, r2=0.8, $fn=6);

            // Branch 2
            translate([0, 0, 6])
                rotate([0, -10, 30])
