// Jinn - Realistic Style (Medium Complexity)
// A 3D printable representation of a mythical jinn with smoke-like lower body

// Color definitions
$fn = 50;

// Main module
module jinn() {
    // Body proportions
    scale(0.8) {
        union() {
            // Upper torso in humanoid form
            color("darkgoldenrod") {
                torso();

                // Arms
                left_arm();
                right_arm();

                // Head with mystical features
                head();

                // Mystical smoke base (lower body)
                smoke_base();

                // Decorative elements
                mystical_details();
            }
        }
    }
}

// Torso module
module torso() {
    translate([0, 0, 15]) {
        difference() {
            // Main torso
            scale([1.2, 0.8, 1.5]) 
                sphere(8);

            // Neck hole
            translate([0, 0, 5])
                cylinder(h=10, r=3, center=true);
        }

        // Chest details
