    color(color_mane)
    translate([body_length/2 + 5, 0, -5])
    rotate([0, 20, 0])
    union() {
        // Tail base
        color(color_body)
        rotate([0, 90, 0])
        cylinder(h=15, d1=8, d2=5, $fn=8);

        // Hair strands
        for(i=[0:15:45]) {
            rotate([0, 0, i])
            translate([15, 0, 0])
            hair_strand(20 - i/3, 2);
        }
    }
}

// Final assembly
