    union() {
        // Upper leg
        translate([x, 0, z])
            rotate([0, angle, 0])
            cylinder(h=15, r=3, $fn=16);

        // Lower leg
        translate([x + sin(angle)*15, 0, z - cos(angle)*15])
            rotate([0, angle, 0])
            cylinder(h=12, r=2, $fn=16);

        // Hoof
        translate([x + sin(angle)*27, 0, z - cos(angle)*27])
            scale([1, 0.8, 0.5])
            sphere(2.5, $fn=16);
    }
}

// All four legs
