// Merlion - Singapore's mythical creature with lion head and fish body
// Designed for 3D printing at 80mm height

$fn = 32; // Smooth curves for better print quality

module merlion(scale_factor = 1) {
    color("goldenrod") {
        // Lion head - main structure
        translate([0, 0, 40 * scale_factor]) {
            difference() {
                sphere(15 * scale_factor); // Main head

                // Eye sockets
                translate([8 * scale_factor, 12 * scale_factor, 5 * scale_factor]) {
                    sphere(3 * scale_factor);
                }
                translate([-8 * scale_factor, 12 * scale_factor, 5 * scale_factor]) {
                    sphere(3 * scale_factor);
                }

                // Nostrils
                translate([2 * scale_factor, 14 * scale_factor, -5 * scale_factor]) {
                    rotate([90, 0, 0]) cylinder(2 * scale_factor, 1 * scale_factor, 1 * scale_factor);
                }
                translate([-2 * scale_factor, 14 * scale_factor, -5 * scale_factor]) {
                    rotate([90, 0, 0]) cylinder(2 * scale_factor, 1 * scale_factor, 1 * scale_factor);
                }
            }

            // Mane - layered cylinders for realistic effect
            for(i = [0:30]) {
                rotate([0, 0, i * 12]) {
                    translate([12 * scale_factor + sin(i * 30) * 3, 0, 0]) {
                        rotate([90, 0, 0]) 
                        cylinder(8 * scale_factor, 2 * scale_factor, 1 * scale_factor);
                    }
                }
            }

            // Eyes
            color("black") {
                translate([8 * scale_factor, 12 * scale_factor, 5 * scale_factor]) {
                    sphere(1.5 * scale_factor);
                }
                translate([-8 * scale_factor, 12 * scale_factor, 5 * scale_factor]) {
                    sphere(1.5 * scale_factor);
                }
            }

            // Nose
            translate([0, 15 * scale_factor, 0]) {
                scale([3 * scale_factor, 2 * scale_factor, 2 * scale_factor]) {
                    sphere(1.5 * scale_factor);
                }
            }

            // Mouth
            translate([0, 12 * scale_factor, -8 * scale_factor]) {
                rotate([90, 0, 0]) {
                    linear_extrude(2 * scale_factor) {
                        difference() {
                            circle(8 * scale_factor);
                            circle(6 * scale_factor);
                        }
                    }
                }
            }
        }

        // Fish body - streamlined shape
        translate([0, 0, 20 * scale_factor]) {
            scale([1, 0.6, 2]) {
                sphere(15 * scale_factor);
            }
        }

        // Tail - fish tail with realistic curves
        translate([0, 0, 5 * scale_factor]) {
            rotate([0, 0, 0]) {
                difference() {
                    union() {
                        // Main tail base
                        scale([1.2 * scale_factor, 0.8 * scale_factor, 2 * scale_factor]) {
                            cylinder(15 * scale_factor, 10 * scale_factor, 5 * scale_factor);
                        }

                        // Tail fins
                        translate([0, 8 * scale_factor, -10 * scale_factor]) {
                            rotate([0, 15, 0]) {
                                scale([1 * scale_factor, 0.5 * scale_factor, 2 * scale_factor]) {
                                    cylinder(8 * scale_factor, 5 * scale_factor, 1 * scale_factor);
                                }
                            }
                        }

                        translate([0, -8 * scale_factor, -10 * scale_factor]) {
                            rotate([0, -15, 0]) {
                                scale([1 * scale_factor, 0.5 * scale_factor, 2 * scale_factor]) {
                                    cylinder(8 * scale_factor, 5 * scale_factor, 1 * scale_factor);
                                }
                            }
                        }
                    }

                    // Hollow out tail for printing
                    translate([0, 0, 5 * scale_factor]) {
                        cylinder(20 * scale_factor, 8 * scale_factor, 3 * scale_factor);
                    }
                }
            }
        }

        // Dorsal fin - fish scales pattern
        translate([0, 0, 30 * scale_factor]) {
            for(i = [0:5]) {
                translate([0, 0, i * 2 * scale_factor]) {
                    scale([0.5 * scale_factor, 0.2 * scale_factor, 1 * scale_factor]) {
                        rotate([0, 0, i * 15]) {
                            cylinder(5 * scale_factor, 3 * scale_factor, 1 * scale_factor);
                        }
                    }
                }
            }
        }

        // Pectoral fins
        translate([10 * scale_factor, 0, 25 * scale_factor]) {
            rotate([0, 0, 45]) {
                scale([1 * scale_factor, 0.3 * scale_factor, 1.5 * scale_factor]) {
                    cylinder(5 * scale_factor, 4 * scale_factor, 2 * scale_factor);
                }
            }
        }

        translate([-10 * scale_factor, 0, 25 * scale_factor]) {
            rotate([0, 0, -45]) {
                scale([1 * scale_factor, 0.3 * scale_factor, 1.5 * scale_factor]) {
                    cylinder(5 * scale_factor, 4 * scale_factor, 2 * scale_factor);
                }
            }
        }

        // Water spout from mouth - simplified for printing
        translate([0, 15 * scale_factor, 45 * scale_factor]) {
            color("lightblue") {
                rotate([90, 0, 0]) {
                    cylinder(8 * scale_factor, 2 * scale_factor, 1 * scale_factor);
                }
            }
        }
    }
}

