// Ogopogo - Lake Monster from Canadian folklore
// Realistic style, medium complexity

// Color definitions
body_color = "darkgreen";
fin_color = "seagreen";
horn_color = "goldenrod";
eye_color = "red";

// Main module for Ogopogo
module ogopogo() {
    // Body segments
    union() {
        // Head
        translate([0, 0, 15]) {
            color(body_color) {
                // Main head shape
                sphere(8, $fn=32);

                // Snout
                translate([7, 0, 0])
                    scale([2, 1, 0.8])
                    sphere(4, $fn=16);

                // Horns
                translate([2, 5, 8])
                    rotate([30, 0, 45])
                    color(horn_color)
                    cylinder(h=12, r1=1.5, r2=0.5, $fn=8);

                translate([2, -5, 8])
                    rotate([30, 0, -45])
                    color(horn_color)
                    cylinder(h=12, r1=1.5, r2=0.5, $fn=8);

                // Eyes
                translate([6, 3, 3])
                    color(eye_color)
                    sphere(1.5, $fn=16);

                translate([6, -3, 3])
                    color(eye_color)
                    sphere(1.5, $fn=16);
            }
        }

        // Neck
        translate([-5, 0, 10])
            color(body_color)
            scale([1.5, 1, 0.8])
            rotate([0, 10, 0])
            cylinder(h=10, r1=4, r2=3, $fn=16);

        // Body segments
        for(i = [0:4]) {
            translate([-15 - i*8, 0, 8 - i*1.5]) {
                color(body_color) {
                    // Main body segment
                    scale([1, 0.8, 0.6])
                    sphere(7 - i*0.8, $fn=24);

                    // Dorsal fin
                    if(i < 3) {
                        translate([-2, 0, 3])
                            color(fin_color)
                            rotate([0, 90, 0])
                            linear_extrude(height=0.5)
                            polygon(points=[[0,0], [4,3], [8,0], [4,-3]]);
                    }

                    // Side fins
                    if(i < 4) {
                        translate([0, 4 + i*0.5, -1])
                            color(fin_color)
                            rotate([0, 0, 20])
                            scale([1.2, 0.8, 0.3])
                            sphere(2, $fn=16);

                        translate([0, -4 - i*0.5, -1])
                            color(fin_color)
                            rotate([0, 0, -20])
                            scale([1.2, 0.8, 0.3])
                            sphere(2, $fn=16);
                    }
                }
            }
        }

        // Tail
        translate([-55, 0, 0]) {
            color(body_color) {
                // Tail connection
                sphere(3, $fn=16);

                // Tail segments
                for(j = [0:3]) {
                    translate([-j*5, 0, 0]) {
                        scale([1 - j*0.2, 1 - j*0.2, 0.3])
                        rotate([0, j*10, 0])
                        sphere(3 - j*0.5, $fn=16);

                        // Tail fin
                        if(j == 3) {
                            translate([-2, 0, 0])
                                color(fin_color)
                                rotate([0, 90, 0])
                                linear_extrude(height=0.5)
                                polygon(points=[[0,0], [6,4], [12,0], [6,-4]]);
                        }
                    }
                }
            }
        }

        // Underside scales pattern
        for(seg = [0:4]) {
            for(sc = [0:3]) {
                translate([-12 - seg*10, sc*2 - 3, 3 - seg*0.5]) {
                    color(body_color)
                    scale([0.8, 0.5, 0.2])
                    sphere(1, $fn=8);
                }
            }
        }
    }
}

