// Scylla - Mythical Sea Monster with Dog Heads and Serpentine Tails
// Size: ~70mm tall, 3D printable

// Main body module
module scylla_body() {
    color("darkseagreen")
    union() {
        // Torso - humanoid upper body
        translate([0, 0, 20])
            scale([1.2, 0.8, 2])
            sphere(r=8, $fn=20);

        // Waist transition
        translate([0, 0, 10])
            cylinder(h=10, r1=7, r2=9, $fn=16);

        // Lower body - serpentine mass
        translate([0, 0, 0])
            scale([1.5, 1, 1.5])
            sphere(r=10, $fn=20);
    }
}

// Dog head module
module dog_head(angle, height) {
    color("saddlebrown")
    translate([12*cos(angle), 12*sin(angle), height])
    rotate([0, 0, angle*180/PI])
    union() {
        // Snout
        translate([5, 0, 0])
            scale([2, 1, 1])
            sphere(r=3, $fn=16);

        // Head
        sphere(r=4, $fn=16);

        // Ears
        translate([-2, 3, 2])
            rotate([30, 0, 0])
            scale([0.5, 2, 1])
            cylinder(h=3, r=1.5, $fn=8);

        translate([-2, -3, 2])
            rotate([-30, 0, 0])
            scale([0.5, 2, 1])
            cylinder(h=3, r=1.5, $fn=8);

        // Eyes
        color("red")
        translate([2, 1.5, 1])
            sphere(r=0.8);
        translate([2, -1.5, 1])
            sphere(r=0.8);
    }
}

// Tentacle/dog leg module
module tentacle(length, angle, segments) {
    color("darkolivegreen")
    translate([15*cos(angle), 15*sin(angle), 5])
    rotate([0, 0, angle*180/PI])
    for(i=[0:segments-1]) {
        translate([i*length/segments, 0, -i*2])
        rotate([0, 20, 0])
        cylinder(h=length/segments+1, r1=3-i*0.2, r2=2.8-i*0.2, $fn=8);
    }
}

// Serpentine tail module
module serpent_tail() {
    color("darkgreen")
    translate([0, 0, -5])
    for(i=[0:5]) {
        translate([0, 0, -i*4])
        rotate([0, i*15, sin(i*30)*20])
        scale([1-i*0.1, 1-i*0.1, 1])
        cylinder(h=4, r1=5-i*0.5, r2=4.5-i*0.5, $fn=12);
    }
}

// Arms module
module arms() {
    color("darkseagreen")
    union() {
        // Left arm
        translate([7, 6, 30])
        rotate([0, 0, 45])
        union() {
            cylinder(h=10, r=2, $fn=12);
            translate([0, 0, 10])
                sphere(r=2.5);
        }

        // Right arm
        translate([7, -6, 30])
        rotate([0, 0, -45])
        union() {
            cylinder(h=10, r=2, $fn=12);
            translate([0, 0, 10])
                sphere(r=2.5);
        }
    }
}

// Main assembly
union() {
    scylla_body();

    // Six dog heads around waist
    for(i=[0:5]) {
        dog_head(i*60*PI/180, 25);
    }

    // Twelve tentacles/legs
    for(i=[0:11]) {
        tentacle(20, i*30*PI/180, 5);
    }

    // Serpentine tail
    serpent_tail();

    // Arms
    arms();
}

