color_horn = [0.9, 0.85, 0.75];        // Ivory
color_eye = [1, 0.2, 0.2];             // Fiery red
color_accent = [0.7, 0.7, 0.8];        // Light scale highlights

// Scale module for repetitive pattern
module scale(x, y, z, size) {
    translate([x, y, z]) {
        scale([size, size, 0.5]) {
            color(color_accent) sphere(1);
        }
    }
}

// Horn module
module horn(base_r, height, twist) {
    color(color_horn) {
        for(i = [0:5]) {
            rotate([0, 0, twist * i]) {
                translate([0, 0, i * height/6]) {
                    cylinder(h = height/6, r1 = base_r * (1 - i/6), r2 = base_r * (1 - (i+1)/6));
                }
            }
        }
    }
}

// Wing membrane
module wing() {
    color(color_wing) {
        difference() {
            union() {
                // Main wing shape
                translate([-15, 0, 0]) {
                    rotate([0, 90, 0]) {
                        scale([40, 25, 2]) {
                            sphere(1);
                        }
                    }
                }

                // Wing finger supports
                for(i = [0:3]) {
                    rotate([0, 0, i * 25]) {
                        translate([5 + i*8, 0, 0]) {
                            rotate([0, 90, 0]) {
                                cylinder(h = 30 - i*5, r = 1.5);
                            }
                        }
                    }
                }
            }

            // Wing holes
            for(i = [0:2]) {
                for(j = [0:1]) {
                    translate([10 + i*12, -10 + j*20, 0]) {
                        sphere(3);
                    }
                }
            }
        }
    }
}

// Dragon head
module head() {
    color(color_dragon) {
        union() {
            // Snout
            translate([0, 0, 10]) {
                scale([1.5, 1, 1]) {
                    sphere(8);
                }
            }

            // Main head
            sphere(12);

            // Lower jaw
            translate([0, 0, -5]) {
                rotate([15, 0, 0]) {
                    scale([0.8, 0.6, 0.5]) {
                        sphere(10);
                    }
                }
            }

            // Eyes
            translate([7, 5, 8]) {
                color(color_eye) sphere(2);
            }
            translate([7, -5, 8]) {
                color(color_eye) sphere(2);
            }

            // Horns
            translate([5, 6, 15]) {
                horn(2, 12, 15);
            }
            translate([5, -6, 15]) {
                horn(2, 12, -15);
            }

            // Nostrils
            translate([12, 2, 6]) {
                rotate([90, 0, 0]) {
                    cylinder(h = 3, r1 = 2, r2 = 0);
                }
            }
            translate([12, -2, 6]) {
                rotate([90, 0, 0]) {
                    cylinder(h = 3, r1 = 2, r2 = 0);
                }
            }
        }
    }
}

// Serpentine body segment
module body_segment(radius, length, scale_factor) {
    color(color_dragon) {
        for(i = [0:3]) {
            translate([i * length/4, 0, 0]) {
                scale([scale_factor, scale_factor, scale_factor]) {
                    sphere(radius);
                }
            }
        }

        // Connect segments
        for(i = [0:2]) {
            translate([i * length/4 + length/8, 0, 0]) {
                rotate([90, 0, 0]) {
                    cylinder(h = length/4, r = radius * 0.8);
                }
            }
        }

        // Scales
        for(i = [0:5]) {
            scale(i * 2 - 5, 0, 5, 1.5);
            scale(i * 2 - 5, radius * 0.7, 5, 1);
            scale(i * 2 - 5, -radius * 0.7, 5, 1);
        }
    }
}

// Tail
module tail() {
    color(color_dragon) {
        union() {
            // Main tail segments
            for(i = [0:4]) {
                translate([i * 8, 0, 0]) {
                    scale([1 - i*0.15, 1 - i*0.15, 1 - i*0.15]) {
