            translate([i*6, 5, 5]) rotate([90, 0, 0]) 
            scale([1.2, 1.2, 0.3]) cylinder(h=3, r1=2, r2=3);
        }
    }

    // Snake coils around body
    module snake_coil(height, radius) {
        for(i = [0:359]) {
            translate([
                radius * cos(i*2) + 5 * sin(i*4),
                radius * sin(i*2),
                height + i*0.2
            ]) rotate([90, 0, i*2]) 
            scale([0.8, 0.8, 1]) cylinder(h=2, r1=1.5, r2=1);
        }
    }

    // Multiple serpent heads
    module serpent_head(offset, angle) {
        translate(offset) rotate(angle) {
            // Neck
            rotate([0, 90, 0]) cylinder(h=8, r1=2, r2=1.5);

            // Head
            translate([8, 0, 0]) scale([2, 1, 1]) sphere(2.5);

            // Eyes
            color([1, 0, 0]) {
                translate([9.5, 1, 1]) sphere(0.5);
                translate([9.5, -1, 1]) sphere(0.5);
            }

            // Forked tongue
            translate([10.5, 0, -0.5]) rotate([0, 0, 30]) 
            scale([0.1, 0.1, 2]) cube([1, 1, 1]);
            translate([10.5, 0, -0.5]) rotate([0, 0, -30]) 
            scale([0.1, 0.1, 2]) cube([1, 1, 1]);
        }
    }

    // Main head with horns
    module main_head() {
        translate([0, 0, 25]) {
            // Head base
            scale([1.5, 1.2, 1.8]) sphere(8);

            // Snout
            translate([10, 0, -2]) scale([2, 1, 1]) sphere(5);

            // Horns
            color([0.1, 0.05, 0]) {
                // Left horn
                translate([5, 8, 10]) rotate([30, 0, -30]) 
                cylinder(h=12, r1=2, r2=0.5);

                // Right horn
                translate([5, -8, 10]) rotate([30, 0, 30]) 
                cylinder(h=12, r1=2, r2=0.5);

                // Central horn
                translate([8, 0, 15]) rotate([15, 0, 0]) 
                cylinder(h=15, r1=3, r2=1);
            }

            // Eyes
            color([1, 0.2, 0.2]) {
                translate([8, 4, 5]) sphere(2);
                translate([8, -4, 5]) sphere(2);
            }
        }
    }

    // Wings
    module wing(side) {
        translate([0, side*12, 15]) rotate([0, 0, side*20]) {
            // Wing membrane
            scale([2, 1, 0.1]) sphere(10);

            // Wing bones
            color([0.15, 0.08, 0.03]) {
                translate([10, 0, 0]) rotate([0, 0, 30*side]) 
                cylinder(h=15, r1=1, r2=0.5);

                translate([10, 0, 0]) rotate([0, 0, -30*side]) 
                cylinder(h=15, r1=1, r2=0.5);

                translate([10, 0, 0]) rotate([0, 0, 0]) 
                cylinder(h=15, r1=1, r2=0.5);
            }
        }
    }

    // Legs
    module leg(side) {
        translate([0, side*8, -15]) {
            // Upper leg
            rotate([30, 0, 0]) cylinder(h=12, r1=3, r2=2);

            // Lower leg
            translate([0, 0, -13]) rotate([30, 0, 0]) 
            cylinder(h=10, r1=2, r2=1.5);

            // Foot
            translate([0, 0, -24]) rotate([90, 0, 0]) {
                difference() {
                    scale([2, 1, 1]) sphere(3);
                    translate([0, 0, -2]) cube([4, 2, 2], center=true);
                }

                // Claws
                for(i = [-1, 0, 1]) {
                    translate([i*1.5, 2, 0]) rotate([0, 0, 45]) 
                    cylinder(h=2, r1=0.5, r2=0.2);
                }
            }
        }
    }

    // Tail
    module tail() {
        translate([0, 0, -20]) rotate([30, 0, 0]) {
            for(i = [0:5]) {
                translate([0, 0, -i*5]) rotate([i*5, 0, 0]) {
                    scale([1-i*0.1, 1-i*0.1, 1]) 
                    sphere(4-i);

                    // Tail spikes
                    for(j = [0:3]) {
                        translate([0, 0, 0]) rotate([0, 0, j*90]) {
                            translate([3-i*0.3, 0, 0]) 
                            rotate([90, 0, 0]) 
                            cylinder(h=3-i*0.2, r1=0.5, r2=0);
                        }
                    }
                }
            }
        }
    }

    // Assemble the creature
    torso();

    // Add snake coils
    snake_coil(-5, 18);

    // Main head
    main_head();

    // Serpent heads around main head
    serpent_head([5, 6, 20], [0, 0, 30]);
    serpent_head([5, -6, 20], [0, 0, -30]);
    serpent_head([-3, 8, 18], [0, 0, 60]);
    serpent_head([-3, -8, 18], [0, 0, -60]);

    // Wings
    wing(1);
    wing(-1);

    // Legs
    leg(1);
    leg(-1);

    // Tail
    tail();
}

