module horn() {
    color(horn_color)
    translate([body_length/2+8, 0, leg_height+body_height/2+head_radius])
    cylinder(h=horn_height, r1=horn_base, r2=0.5);

    // Spiral ridges
    for(i=[0:30:360]) {
        rotate([0, 0, i])
        translate([horn_base*0.7, 0, 0])
        rotate_extrude(angle=30, $fn=8)
        translate([0, 0])
        square([0.5, horn_height/12]);
    }
}

module legs() {
    color(fur_color)
    union() {
        // Front legs
        for(side=[-1, 1]) {
            translate([body_length/3, side*body_width/2, 0])
            cylinder(h=leg_height, r=leg_width/2);

            // Paw
            translate([body_length/3, side*body_width/2, 0])
            sphere(d=leg_width*1.2);
        }

        // Back legs
        for(side=[-1, 1]) {
            translate([-body_length/3, side*body_width/2, 0])
            cylinder(h=leg_height*0.9, r=leg_width/2);

            // Paw
            translate([-body_length/3, side*body_width/2, 0])
            sphere(d=leg_width*1.2);
        }
    }
}

module wings() {
    color(wing_color)
    union() {
        // Left wing
        translate([-body_length/4, body_width/2, leg_height+body_height/2+5])
        rotate([0, -30, 0])
        difference() {
            // Main wing shape
            scale([1, 0.3, 0.8])
            sphere(d=wing_span);

            // Wing cutouts
            for(i=[1:3]) {
                translate([i*8-20, 0, 5-i*3])
                scale([0.3, 1, 0.5])
                sphere(d=wing_span/2);
            }
        }

        // Right wing (mirrored)
        translate([-body_length/4, -body_width/2, leg_height+body_height/2+5])
        rotate([0, -30, 180])
        difference() {
            scale([1, 0.3, 0.8])
            sphere(d=wing_span);

            for(i=[1:3]) {
                translate([i*8-20, 0, 5-i*3])
                scale([0.3, 1, 0.5])
                sphere(d=wing_span/2);
            }
        }
    }
}

module tail() {
    color(fur_color)
    union() {
        for(i=[0:tail_segments-1]) {
            rotate([0, 0, -i*15])
            translate([-tail_length/2 + i*tail_length/tail_segments, 0, 
                      leg_height + body_height/4 - i*2])
            scale([1-i*0.1, 1-i*0.1, 1-i*0.05])
            sphere(d=body_width/3);
        }

        // Tuft at end
        translate([-tail_length, 0, leg_height + body_height/4 - tail_segments*2])
        scale([0.5, 0.5, 0.8])
        sphere(d=body_width/2);
    }
}

// Whiskers
module whiskers() {
    color("white")
    for(side=[-1, 1]) {
        for(angle=[-20, 0, 20]) {
            translate([body_length/2+20, side*head_radius*0.5, leg_height+body_height/2-3])
            rotate([0, side*angle, 90])
            cylinder(h=10, r=0.3);
        }
    }
}

// Assemble the unicat
union() {
    unicat_body();
    head();
    horn();
    legs();
    wings();
    tail();
    whiskers();
}

