// Color definitions
color_bone = [0.9, 0.85, 0.75];
color_flesh = [0.7, 0.5, 0.4];
color_dark = [0.2, 0.2, 0.2];

// Main module
module wendigo() {
    union() {
        // Body
        body();

        // Head
        translate([0, 0, 25]) head();

        // Arms
        translate([-8, 0, 20]) left_arm();
        translate([8, 0, 20]) mirror([1, 0, 0]) left_arm();

        // Legs
        translate([-3, 0, 5]) left_leg();
        translate([3, 0, 5]) mirror([1, 0, 0]) left_leg();

        // Antlers
        translate([0, 0, 35]) antlers();

        // Ribcage detail
        translate([0, 0, 15]) ribs();
    }
}

// Body - emaciated torso
module body() {
    color(color_flesh) {
        // Main torso
        scale([1, 0.6, 1.2]) sphere(8);

        // Spine ridge
        for(i = [0:8]) {
            translate([0, -3, 5 + i * 2.5]) 
                rotate([90, 0, 0]) 
                    cylinder(h=2, r1=1.5, r2=0.5);
        }
    }
}

// Head - skull-like with elongated snout
module head() {
    color(color_bone) {
        // Main skull
        difference() {
            scale([0.8, 0.7, 1.2]) sphere(5);
            // Eye sockets
            translate([-2, 2.5, 1]) sphere(1.5);
            translate([2, 2.5, 1]) sphere(1.5);
            // Nasal cavity
            translate([0, 3, -1]) scale([1, 0.5, 0.5]) sphere(2);
        }

        // Snout
        translate([0, 4, -2]) scale([0.8, 1.5, 0.8]) sphere(3);

        // Teeth
        for(i = [-2:2]) {
            translate([i * 0.8, 5.5, -3]) 
                cylinder(h=2, r=0.3);
        }
    }
}

// Antlers - branching bone structures
module antlers() {
    color(color_bone) {
        // Left antler
        translate([-3, 0, 0]) {
            cylinder(h=8, r=0.8);
            translate([0, 0, 4]) rotate([0, 0, -20]) cylinder(h=6, r=0.6);
            translate([0, 0, 7]) rotate([0, 0, 20]) cylinder(h=4, r=0.4);
        }

        // Right antler
        translate([3, 0, 0]) {
            cylinder(h=8, r=0.8);
            translate([0, 0, 4]) rotate([0, 0, 20]) cylinder(h=6, r=0.6);
            translate([0, 0, 7]) rotate([0, 0, -20]) cylinder(h=4, r=0.4);
        }
    }
}

// Left arm - long and clawed
module left_arm() {
    color(color_flesh) {
        // Upper arm
        rotate([0, 0, -20]) {
            cylinder(h=12, r=1.5);

            // Lower arm
            translate([0, 0, 12]) rotate([0, 0, -30]) {
                cylinder(h=10, r=1.2);

                // Hand with claws
                translate([0, 0, 10]) rotate([0, 0, -45]) {
                    // Palm
                    sphere(1.5);

                    // Claws
                    for(i = [-1:1]) {
                        translate([i * 0.8, 0, 1]) 
                            scale([0.3, 0.3, 2]) 
                                cylinder(h=1, r=1);
                    }
                }
            }
        }
    }
}

// Left leg - digitigrade
module left_leg() {
    color(color_flesh) {
        // Thigh
        rotate([0, 0, 10]) {
            cylinder(h=10, r=1.8);

            // Shin
            translate([0, 0, 10]) rotate([0, 0, -30]) {
                cylinder(h=8, r=1.5);

                // Foot
                translate([0, 0, 8]) rotate([0, 0, 30]) {
                    scale([1.5, 0.8, 0.5]) sphere(2);

                    // Toes with claws
                    for(i = [-1:1]) {
                        translate([i * 0.7, 0, -1]) 
                            scale([0.3, 0.3, 1.5]) 
                                cylinder(h=1, r=1);
                    }
                }
            }
        }
    }
}

