// Xecotcovach - Mayan feathered serpent deity
// Realistic style with medium complexity

$fn = 32;  // Smooth curves

// Main body parameters
body_length = 60;
body_width = 12;
body_height = 15;

// Color definitions
snake_color = "darkgreen";
feather_color = "forestgreen";
eye_color = "red";
horn_color = "black";
scale_color = "olive";

// Main module
module xecotcovach() {
    color(snake_color) {
        // Serpentine body
        union() {
            // Head
            translate([0, 0, body_height/2])
                rotate([0, 0, 0])
                    head();

            // Body segments
            for(i = [1:10]) {
                translate([i * 5.5, 0, body_height/2 - i * 0.5])
                    rotate([0, 0, sin(i * 15) * 5])
                        body_segment(i);
            }

            // Tail
            translate([11 * 5.5, 0, body_height/2 - 5.5])
                rotate([0, 0, 15])
                    tail();
        }

        // Wings
        color(feather_color) {
            translate([15, -8, 10])
                rotate([0, 0, -30])
                    left_wing();
            translate([15, 8, 10])
                rotate([0, 0, 30])
                    mirror([1, 0, 0])
                        left_wing();
        }
    }
}

// Head module
module head() {
    // Main head
    union() {
        scale([1.2, 1, 1])
            sphere(6);

        // Snout
        translate([5, 0, 0])
            scale([1.5, 0.7, 0.7])
                sphere(4);

        // Eyes
        color(eye_color) {
            translate([2.5, -3, 2])
                sphere(1.2);
            translate([2.5, 3, 2])
                sphere(1.2);
        }

        // Horns
        color(horn_color) {
            translate([-2, -2.5, 4])
                rotate([0, 30, -15])
                    cylinder(h=5, r1=1.5, r2=0.5);
            translate([-2, 2.5, 4])
                rotate([0, -30, 15])
                    cylinder(h=5, r1=1.5, r2=0.5);
        }
    }
}

// Body segment module
module body_segment(index) {
    // Tapered body
    scale([1 - index * 0.03, 1, 1 - index * 0.02])
        cylinder(h=body_length/10, r=body_width/2);

    // Scales
    color(scale_color) {
        for(a = [0:45:315]) {
            rotate([0, 0, a])
                translate([0, body_width/2 - 1, 0])
                    scale([1, 0.3, 0.3])
                        sphere(2);
        }
    }
}

// Tail module
module tail() {
    union() {
        // Tapering tail
        cylinder(h=15, r1=5, r2=1);

        // Tail feathers
        color(feather_color) {
            for(i = [0:30:150]) {
                rotate([0, i, 0])
                    translate([0, 0, -7.5])
                        scale([0.3, 0.05, 1])
                            sphere(8);
            }
        }
    }
}

// Wing module
module left_wing() {
    union() {
        // Main wing
        linear_extrude(height=0.5)
            polygon(points=[
                [0, 0],
                [20, 5],
                [25, 0],
                [20, -5],
                [0, 0]
            ]);

        // Wing feathers
        for(i = [1:5]) {
            translate([i * 4, 0, 0])
                rotate([0, 0, sin(i * 30) * 10])
                    scale([1 - i * 0.1, 0.7, 1])
                        feather();
        }
    }
}

// Feather module
module feather() {
    difference() {
        linear_extrude(height=0.3)
            polygon(points=[
                [0, 0],
                [8, 2],
                [10, 0],
                [8, -2],
                [0, 0]
            ]);

        // Feather veins
        for(j = [1:3]) {
            translate([j * 2.5, 0, 0.2])
                rotate([0, 0, 0])
                    scale([0.1, 0.5, 0.1])
                        cube([1, 1, 1]);
        }
    }
}

