        cylinder(h=12 * scale_factor, r=2 * scale_factor);

        // Tarsus (third segment)
        color("saddlebrown") 
        translate([cos(angle)*22, sin(angle)*22*side, 3]) 
        rotate([60, 0, angle + (side==1?90:-90)]) 
        cylinder(h=10 * scale_factor, r=1.5 * scale_factor);
    }
