        // Main membrane
        translate([5, 0, 8])
            rotate([0, 90, 0])
                scale([1, 0.3, 1])
                    sphere(d = 30);

        // Remove lower half to create wing shape
        translate([5, 0, -5])
            cube([50, 50, 10], center = true);
