        // Main wing membrane
        scale([1, 0.3, 1]) sphere(r=span/2);

        // Wing veins
        for(i=[1:3]) {
            translate([span/4*i, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=height, r=0.5);
        }
    }
