// 3D printable mythical creature with ice crystal wings and frost patterns

module ice_crystal(scale_factor=1, rotation=[0,0,0]) {
    color("LightBlue", 0.7)
    rotate(rotation)
    scale([scale_factor, scale_factor, scale_factor])
    union() {
        // Main crystal body
        cube([2, 2, 8], center=true);

        // Crystal facets
        translate([1, 1, 2]) rotate([45,0,0]) cube([1.5, 1.5, 4], center=true);
        translate([-1, -1, 2]) rotate([45,0,0]) cube([1.5, 1.5, 4], center=true);
        translate([1, -1, -2]) rotate([-45,0,0]) cube([1.5, 1.5, 4], center=true);
        translate([-1, 1, -2]) rotate([-45,0,0]) cube([1.5, 1.5, 4], center=true);
    }
}

module wing() {
    color("LightBlue", 0.6)
    translate([0, 0, 0])
    union() {
        // Main wing membrane
        scale([1, 3, 0.2])
        sphere(5);

        // Ice crystals along wing edge
        for(i = [0:30:120]) {
            rotate([0, 0, i])
            translate([0, 12, 0])
            ice_crystal(0.3, [90,0,0]);
        }
    }
}

module tail_segment(radius, height, z_offset) {
    color("LightCyan", 0.8)
    translate([0, 0, z_offset])
    cylinder(h=height, r=radius, $fn=8);
}

module tail() {
    // Segmented tail with decreasing size
    for(i = [0:1:5]) {
        tail_segment(5-i*0.7, 3, -i*2.5);
    }
}

module horn() {
    color("LightBlue", 0.9)
    union() {
        // Base
        cylinder(h=3, r1=2, r2=1.5, $fn=6);

        // Spiral
        translate([0, 0, 3])
        cylinder(h=6, r1=1.5, r2=0.5, $fn=6);
    }
}

module body() {
    color("LightCyan", 0.85)
    union() {
        // Torso
        scale([1, 1.2, 1.5])
        sphere(8);

        // Head
        translate([0, 0, 12])
        scale([0.8, 0.8, 1])
        sphere(6);

        // Horns
        translate([3, 0, 15])
    rotate([30, 0, 30])
    horn();
        translate([-3, 0, 15])
    rotate([30, 0, -30])
    horn();
    }
}

module arms() {
    color("LightCyan", 0.85)
    union() {
        // Left arm
        translate([8, 0, 8])
    rotate([0, 45, 0])
    scale([1, 1, 3])
    sphere(2);

        // Right arm
        translate([-8, 0, 8])
    rotate([0, -45, 0])
    scale([1, 1, 3])
    sphere(2);
    }
}

module legs() {
    color("LightCyan", 0.85)
    union() {
        // Left leg
        translate([3, 0, -10])
    rotate([0, 0, 0])
    cylinder(h=10, r=2, $fn=8);

        // Right leg
        translate([-3, 0, -10])
    rotate([0, 0, 0])
    cylinder(h=10, r=2, $fn=8);
    }
}

// Main assembly
union() {
    // Center the model
    translate([0, 0, 10])
    union() {
        // Body parts
        body();
        arms();
        legs();
        tail();

        // Wings
        translate([0, 0, 6])
        rotate([0, 0, 30])
        wing();

        translate([0, 0, 6])
        rotate([0, 0, -30])
        mirror([1,0,0])
        wing();

        // Ice aura effect
        color("White", 0.3)
        translate([0, 0, 5])
        scale([1.2, 1.2, 1.2])
        sphere(10);
    }
}

// Hazelshade - Mythical forest guardian
// A serpentine dragon with bark-textured wings and glowing amber eyes

// Main colors
body_color = "#3D2817";  // Dark bark brown
wing_color = "#5D4E37";  // Lighter bark
eye_color = "#FF8C00";   // Amber glow
accent_color = "#8B4513"; // Branch details

// Overall dimensions
scale_factor = 0.8;  // Scales model to ~80mm length

// Create the full creature
union() {
    translate([0, 0, 5]) scale(scale_factor) {
        // Body segments
        body();

        // Wings
        wings();

        // Head
        head();

        // Tail
        tail();

        // Legs
        legs();
    }
}

// Main body module
module body() {
    color(body_color) {
        // Torso - elongated oval
        rotate([90, 0, 0]) 
            scale([1.2, 1, 1.5])
            sphere(8, $fn=20);

        // Neck connection
        translate([0, 0, 12])
            rotate([90, 0, 0])
            scale([0.8, 0.8, 1.2])
            cylinder(6, 5, 3, $fn=12);
    }
}

// Bark-textured wings
module wings() {
    color(wing_color) {
        // Left wing
        translate([-8, 0, 8]) {
            rotate([0, 30, -20]) {
                difference() {
                    // Main wing membrane
                    scale([1.5, 1, 0.1])
                    sphere(8, $fn=6);

                    // Wing holes for texture
                    for(i = [1:5]) {
                        translate([i*2-5, i, 0])
                        scale([0.3, 0.3, 2])
                        sphere(2, $fn=8);
                    }
                }

                // Wing arm
                color(accent_color)
                translate([6, 0, 0])
                rotate([0, 0, 90])
                cylinder(12, 1, 0.5, $fn=8);
            }
        }

        // Right wing (mirrored)
        mirror([1, 0, 0]) {
            translate([-8, 0, 8]) {
                rotate([0, 30, -20]) {
                    difference() {
                        scale([1.5, 1, 0.1])
                        sphere(8, $fn=6);

                        for(i = [1:5]) {
                            translate([i*2-5, i, 0])
                            scale([0.3, 0.3, 2])
                            sphere(2, $fn=8);
                        }
                    }

                    color(accent_color)
                    translate([6, 0, 0])
                    rotate([0, 0, 90])
                    cylinder(12, 1, 0.5, $fn=8);
                }
            }
        }
    }
}

// Dragon head
module head() {
    color(body_color) {
        // Snout
        translate([0, 0, 20])
        rotate([90, 0, 0])
        scale([0.8, 0.6, 1.2])
        cylinder(8, 4, 2, $fn=12);

        // Head base
        translate([0, 0, 15])
        sphere(5, $fn=16);

        // Horns
        color(accent_color) {
            translate([2, 3, 17])
            rotate([30, 0, 30])
            cylinder(6, 1, 0.5, $fn=6);

            translate([-2, 3, 17])
            rotate([30, 0, -30])
            cylinder(6, 1, 0.5, $fn=6);
        }

        // Eyes - glowing amber
        color(eye_color) {
            translate([2.5, 2.5, 17])
            sphere(1.2, $fn=8);

            translate([-2.5, 2.5, 17])
            sphere(1.2, $fn=8);
        }
    }
}

// Serpentine tail
module tail() {
    color(body_color) {
        // Segmented tail
        for(i = [1:8]) {
            translate([0, 0, -i*3.5])
            rotate([0, 0, i*15])
            scale([1 - i*0.08, 1 - i*0.08, 1])
            sphere(4, $fn=12);
        }

        // Tail tip
        translate([0, 0, -30])
        scale([0.3, 0.3, 1])
        sphere(3, $fn=8);
    }
}

// Four legs
module legs() {
    color(body_color) {
        // Front legs
        for(side = [-1, 1]) {
            translate([side*6, 0, 5]) {
                rotate([0, 30, 0])
                cylinder(8, 1.5, 1, $fn=8);

                // Feet
                translate([0, 0, -8.5])
                scale([1.5, 2, 0.5])
                sphere(2, $fn=8);
            }
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([side*5, 0, -5]) {
                rotate([0, -30, 0])
                cylinder(7, 1.5, 1.2, $fn=8);

                // Feet
                translate([0, 0, -7.5])
                scale([1.5, 2, 0.5])
                sphere(2, $fn=8);
            }
        }
    }
}

// IvySprite - A mythical forest sprite made of living ivy
// Total height: 80mm, wingspan: 60mm

$fn = 32;

// Main module
module ivySprite() {
    // Root ball base for stability
    color("darkgreen") translate([0,0,0]) sphere(d=15);

    // Ivy-covered body
    ivyBody();

    // Wings
    wings();

    // Head with leaf hair
    head();

    // Arms made of twisted vines
    arms();

    // Magical glow orbs
    glowOrbs();
}

module ivyBody() {
    // Tapered torso made of vines
    for(i=[0:5]) {
        color("forestgreen") 
        rotate([0,0,i*60]) 
        translate([0,0,i*8+10]) 
        cylinder(h=12, d1=12-i, d2=10-i, center=true);
    }

    // Ivy leaf scales
    for(i=[0:20]) {
        angle = i * 18;
        height = 15 + (i % 3) * 8;
        color("limegreen") 
        rotate([0,0,angle]) 
        translate([7,0,height]) 
        scale([0.8,0.3,0.5]) 
        sphere(d=6);
    }
}

module wings() {
