    color("seagreen") 
    translate([12,0,55]) 
    rotate([0,0,30]) 
    mirror([1,0,0]) 
    scale([1,0.2,1]) 
    difference() {
        sphere(d=20);
        translate([5,0,0]) sphere(d=18);
