// Lunarshade - Mythical creature of moonlight and shadow
// Realistic style with medium complexity

$fa = 2;
$fs = 0.5;

// Main creature module
module lunarshade() {
    // Body - elongated crescent shape
    color("slateblue") {
        rotate([90, 0, 0]) 
            scale([1.5, 0.8, 2]) 
                sphere(d=20, $fn=32);
    }

    // Head - angular with lunar features
    translate([0, 0, 22]) {
        color("midnightblue") {
            rotate([90, 0, 0])
                difference() {
                    scale([1.2, 1, 1.3]) sphere(d=15, $fn=24);
                    translate([-8, -8, -8]) cube([16, 16, 16]);
                }

            // Crescent moon horns
            rotate([0, -30, 0])
                translate([0, 6, 0])
                    scale([1, 0.4, 0.2])
                        cylinder(h=12, d=8, $fn=3);

            rotate([0, 30, 0])
                translate([0, -6, 0])
                    scale([1, 0.4, 0.2])
                        cylinder(h=12, d=8, $fn=3);

            // Eyes - glowing orbs
            translate([4, 3, 2])
                scale([0.8, 0.8, 0.8])
                    color("cyan") sphere(d=3, $fn=12);
            translate([-4, 3, 2])
                scale([0.8, 0.8, 0.8])
                    color("cyan") sphere(d=3, $fn=12);
        }
    }

    // Wings - ethereal membrane wings
    module wing(side) {
        translate([side * 15, 0, 15]) {
            rotate([0, 0, side * 20]) {
                color("lavender", 0.6) {
                    // Wing membrane
                    difference() {
                        scale([2, 1.5, 0.3])
                            sphere(d=25, $fn=6);
                        translate([0, 0, -1])
                            cylinder(h=3, d=20, $fn=6);
                    }

                    // Wing bones
                    for(i = [0:30:120]) {
                        rotate([0, 0, i * side])
                            translate([0, 0, 0.5])
                                color("darkslateblue")
                                    cube([18, 0.5, 1]);
                    }
                }
            }
        }
    }

    wing(1);
    wing(-1);

    // Tail - flowing shadow tail
    translate([0, 0, -25]) {
        color("indigo") {
            rotate([0, 0, 0])
                for(i = [0:5:30]) {
                    translate([0, sin(i*3)*3, -i*1.5])
                        rotate([0, 0, sin(i*3)*10])
                            scale([1-i/30, 1-i/30, 1])
                                sphere(d=8-i/4, $fn=12);
                }
        }
    }

    // Legs - slender with moon markings
    module leg(side) {
        translate([side * 5, 0, -10]) {
            color("slateblue") {
                // Upper leg
                rotate([0, side * 15, 0])
                    cylinder(h=12, d=4, $fn=8);

                // Lower leg
                translate([0, 0, -8])
                    rotate([0, side * -20, 0])
                        cylinder(h=10, d=3, $fn=8);

                // Paw with moon markings
                translate([0, 0, -15])
                    sphere(d=5, $fn=12);

                // Crescent marking
                translate([0, 2, -14])
                    rotate([90, 0, 0])
                        color("silver", 0.7)
                            cylinder(h=0.5, d=3, $fn=3);
            }
        }
    }

    leg(1);
    leg(-1);

    // Moon phases pattern on back
    translate([0, 0, 12]) {
        rotate([90, 0, 0])
            for(i = [0:45:315]) {
                rotate([0, 0, i])
                    translate([0, 5, 0])
                        color("silver", 0.5)
                            scale([1, 1, 0.3])
                                sphere(d=3, $fn=12);
            }
    }

    // Shadow wisps around body
    for(i = [0:45:315]) {
        rotate([0, 0, i])
            translate([8*cos(i*3), 8*sin(i*3), -5])
                color("darkslateblue", 0.3)
                    sphere(d=2, $fn=6);
    }
