module quartz_crystal(scale_factor=1) {
    // Hexagonal crystal prism
    color(crystal) {
        for (i = [0:5]) {
            rotate([0, 0, i * 60]) 
                translate([0, scale_factor * 2, 0])
                cube([scale_factor * 0.5, scale_factor * 4, scale_factor * 0.5], center=true);
        }
        cylinder(h=scale_factor * 6, r=scale_factor * 2, $fn=6);
    }
}

module dragon_head() {
