module dragon_wing() {
    // Wing structure with crystalline membranes
    union() {
        // Wing arm
        color(core)
            rotate([0, 90, 0])
            cylinder(h=15, r=1, $fn=8);

        // Wing membranes
        translate([10, 0, 0]) wing_segment(8, 4, 0.3);
        translate([15, 2, 0]) rotate([0, 0, 20]) wing_segment(6, 3, 0.25);
        translate([18, 3, 0]) rotate([0, 0, 30]) wing_segment(4, 2, 0.2);

