        sphere(d=35, $fn=32);

        // Underside cavity
        translate([0, 0, -5])
        sphere(d=30, $fn=32);
    }

    // Glowing fungal spots on cap
    module fungal_spots(radius) {
        for(i = [0:8]) {
            angle = i * 137.5;  // Golden angle for natural distribution
            dist = 10 + (i*2);
            x = cos(angle) * dist;
            y = sin(angle) * dist;

            translate([x, y, 15])
            color([0.8, 0.4, 0.9])
            sphere(r=radius, $fn=8);
        }
