        translate([0, 0, height])
        rotate([0, 0, angle])
        translate([12, 0, 0])
        rotate([0, 90, 0])
        color([0.35, 0.2, 0.15])
        cylinder(h=15, d=4, $fn=8);

        // Claw twigs
        for(i = [0:2]) {
            rotate([0, 0, angle])
            translate([18, 0, height])
            rotate([0, 45 + i*30, 0])
            color([0.3, 0.15, 0.1])
            cylinder(h=8, d=2, $fn=6);
        }
    }

    branch_arm(45, 25);
    branch_arm(-45, 25);
    branch_arm(135, 30);
    branch_arm(-135, 30);

    // Eyes - hollow sockets with glowing cores
    module eye_socket(x_pos, z_pos) {
        // Socket cavity
        translate([x_pos, 12, z_pos])
        rotate([0, 90, 0])
        color([0.1, 0.1, 0.1])
        cylinder(h=5, d=6, $fn=12);

        // Glowing eye core
        translate([x_pos, 14, z_pos])
        color([0.9, 0.2, 0.1])
        sphere(r=2, $fn=8);
    }

    eye_socket(6, 20);
    eye_socket(-6, 20);

    // Corruption veins running down trunk
    module corruption_vein(start_z, length) {
        for(i = [0:length]) {
            z = start_z - i*2;
            x_offset = sin(i*30)*3;

            translate([x_offset, 0, z])
            color([0.5, 0.1, 0.6])
            sphere(r=1.5, $fn=6);
        }
    }

    corruption_vein(35, 10);
    corruption_vein(30, 8);
    corruption_vein(25, 12);
}

