// Driftshade - Shadow dragon of the void
// A medium-complexity 3D printable model using basic primitives

// Main parameters
scale_factor = 0.8;
body_length = 60 * scale_factor;
body_height = 25 * scale_factor;
wing_span = 70 * scale_factor;

// Color definitions
color_body = [0.15, 0.12, 0.25];      // Deep void purple
color_accents = [0.3, 0.2, 0.4];      // Lighter purple highlights
color_eyes = [0.8, 0.2, 0.3];         // Crimson eyes
color_void = [0.05, 0.05, 0.15];      // Darkest void

module driftshade() {
    union() {
        // Main body
        translate([0, 0, body_height/2])
        color(color_body)
        scale([1.2, 0.8, 0.6])
        sphere(d=body_length);

        // Head
        translate([body_length/2 + 8, 0, body_height/2 + 5])
        color(color_body)
        scale([1.5, 1, 1.2])
        sphere(d=20 * scale_factor);

        // Snout
        translate([body_length/2 + 18, 0, body_height/2 + 5])
        color(color_body)
        rotate([0, 15, 0])
        scale([2, 0.8, 0.6])
        cylinder(h=12 * scale_factor, d1=8 * scale_factor, d2=4 * scale_factor);

        // Eyes (glowing void orbs)
        for(side = [-1, 1]) {
            translate([body_length/2 + 15, side * 6, body_height/2 + 8])
            color(color_eyes)
            sphere(d=4 * scale_factor);

            // Eye sockets
            translate([body_length/2 + 16, side * 6, body_height/2 + 8])
            color(color_void)
            sphere(d=5 * scale_factor);
        }

        // Horns - curved back
        for(side = [-1, 1]) {
            translate([body_length/2 + 5, side * 8, body_height/2 + 12])
            rotate([0, side * 30, -20])
            color(color_accents)
            cylinder(h=15 * scale_factor, d1=4 * scale_factor, d2=1 * scale_factor);
        }

        // Neck ridge scales
        for(i = [0:5]) {
            translate([body_length/2 - i * 6, 0, body_height/2 + 12 + i])
            rotate([0, 0, 0])
            color(color_accents)
            scale([1, 0.3, 1.5])
            sphere(d=6 * scale_factor);
        }

        // Wings - void membranes
        for(side = [-1, 1]) {
            translate([0, side * body_length/3, body_height/2 + 5])
            rotate([0, 0, side * 20])
            wing(side);
        }

        // Tail - segmented
        tail_segments();

        // Legs - clawed
        for(side = [-1, 1]) {
            translate([body_length/4, side * body_length/3, body_height/2 - 8])
            leg(side);
        }

        // Void wisps - magical emanations
        for(i = [0:8]) {
            angle = i * 45;
            distance = 25 + sin(i * 30) * 5;
            translate([
                cos(angle) * distance * scale_factor,
                sin(angle) * distance * scale_factor,
                body_height/2 + 10 + sin(i * 60) * 3
            ])
            color(color_void)
            sphere(d=(2 + sin(i * 45)) * scale_factor);
        }
    }
}

module wing(side) {
    color(color_void)
    difference() {
        // Main wing membrane
        translate([0, 0, 0])
        rotate([0, 90, 0])
        scale([1, wing_span/2, 0.5])
        sphere(d=wing_span);

        // Wing holes - void tears
        for(i = [1:3]) {
            translate([5, i * 8 * side, -2 + i])
            sphere(d=6 * scale_factor);
        }

        // Wing claw
        translate([wing_span/2 - 5, 0, 0])
        rotate([0, 0, 45])
        scale([0.5, 0.3, 0.3])
        cylinder(h=8 * scale_factor, d=4 * scale_factor);
    }
}

module tail_segments() {
    for(i = [0:6]) {
        translate([
            -body_length/2 - i * 8,
            0,
            body_height/2 - i * 2
        ])
        rotate([0, 0, sin(i * 15) * 5])
        scale([1 - i * 0.1, 1 - i * 0.1, 1 - i * 0.1])
        color(color_body)
        sphere(d=12 * scale_factor);

        // Tail spikes
        if(i % 2 == 0) {
            translate([
                -body_length/2 - i * 8,
                0,
                body_height/2 - i * 2 + 3
            ])
            color(color_accents)
            cylinder(h=4 * scale_factor, d1=2 * scale_factor, d2=0.5 * scale_factor);
        }
    }
}

module leg(side) {
    union() {
        // Upper leg
        translate([0, 0, 0])
        rotate([90, 0, side * 10])
        color(color_body)
        cylinder(h=12 * scale_factor, d=6 * scale_factor);

        // Lower leg
        translate([0, 0, -12 * scale_factor])
        rotate([90 + side * 15, 0, side * 5])
        color(color_body)
        cylinder(h=10 * scale_factor, d=5 * scale_factor);

        // Foot with claws
        translate([0, 0, -22 * scale_factor])
        rotate([90, 0, 0])
        color(color_body)
        scale([1, 2, 0.5])
        sphere(d=8 * scale_factor);

        // Claws
        for(j = [-1, 0, 1]) {
            translate([j * 3, -8 * scale_factor, -22 * scale_factor])
            rotate([0, 90, 0])
            color(color_accents)
            cylinder(h=4 * scale_factor, d1=2 * scale_factor, d2=0.5 * scale_factor);
        }
    }
}

