    for(y = [-20:5:20]) {
        for(x = [-15:5:15]) {
            translate([x, y, 20*sin(x/5) + 15]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h = 2, r = 2.5);
                        translate([0, 0, -1]) cylinder(h = 4, r = 2);
                    }
                }
            }
        }
    }
}

// Assembly
color("silver") {
    union() {
        dragon_body();
        dragon_head();
        dragon_wing(1);
        dragon_wing(-1);
        dragon_tail();

        // Legs
        dragon_leg(1);
        dragon_leg(-1);
        translate([5, -5, -15]) dragon_leg(1);
        translate([-5, -5, -15]) dragon_leg(-1);

        // Scale details
        scales();
    }
}

// Internal glow
core_glow();

// IceWhirl - Mythical Arctic Creature
// A swirling elemental of frost and wind with crystalline wings

// Overall scale
scale_factor = 0.4;

module icewhirl() {
    scale([scale_factor, scale_factor, scale_factor]) {

        // Main body - swirling vortex core
        color("LightBlue") union() {
            // Central vortex body
            for (i = [0:15:180]) {
                rotate([0, 0, i]) 
                translate([0, 0, i/3])
                scale([1 - i/400, 1 - i/400, 0.5])
                sphere(d=30 - i/10, $fn=8);
            }
        }

        // Crystalline wings
        color("DeepSkyBlue") {
            // Left wing cluster
            translate([-20, 0, 30]) rotate([0, 30, -30]) wing_cluster();
            // Right wing cluster  
            translate([20, 0, 30]) mirror([1, 0, 0]) wing_cluster();
        }

        // Frost tendrils
        color("AliceBlue") {
            for (angle = [0:60:359]) {
                rotate([0, 0, angle]) 
                translate([15, 0, 5])
                rotate([angle/3, 0, 0])
                tendril();
            }
        }

        // Ice horn crown
        color("LightCyan") {
            for (h = [0:40:320]) {
                rotate([0, 0, h]) 
                translate([0, 0, 45])
                rotate([0, 20, 0])
                ice_horn();
            }
        }

        // Eye - glowing core
        color("White") difference() {
            sphere(d=8, $fn=16);
            translate([0, 0, 4]) sphere(d=6, $fn=16);
        }
        color("DarkBlue") sphere(d=4, $fn=12);
    }
}

// Wing crystal structure
module wing_cluster() {
    for (i = [0:20:120]) {
        rotate([0, i/2, i]) 
        translate([i/4, 0, i/8])
        scale([1 - i/200, 0.2, 1 - i/200])
        difference() {
            cube([15, 2, 25], center=true);
            translate([0, 0, 10]) rotate([0, 30, 0]) cube([12, 3, 15], center=true);
        }
    }
}

// Frost tendril
module tendril() {
    for (seg = [0:5:25]) {
        translate([0, 0, seg])
        scale([1 - seg/40, 1 - seg/40, 1])
        rotate([0, 0, seg*5])
        cylinder(h=3, d1=4-seg/10, d2=3-seg/10, $fn=6);
    }
}

// Ice horn
module ice_horn() {
    cylinder(h=12, d1=3, d2=1, $fn=6);
    translate([0, 0, 12]) sphere(d=2, $fn=8);
}

// Render the complete creature
translate([0, 0, 10]) icewhirl();

// Jadegleam - A mystical serpentine dragon with glowing jade scales
// Realistic style, medium complexity, 3D-printable

$fn = 32;

// Main body
module body() {
    color("darkgreen") {
        // Torso segment 1
        translate([0, 0, 10]) {
            scale([1, 0.6, 0.6]) sphere(8);
        }

        // Torso segment 2
        translate([0, 0, 5]) {
            scale([0.9, 0.5, 0.5]) sphere(7);
        }

        // Tail segments using loop
        for(i = [1:5]) {
            translate([0, 0, 10 - i * 3]) {
                scale([1 - i * 0.15, 0.5 - i * 0.08, 0.5 - i * 0.08]) 
                sphere(6 - i);
            }
        }
    }
}

// Head with snout and horns
module head() {
    color("seagreen") {
        // Main head sphere
        translate([0, 0, 18]) {
            scale([1.2, 0.8, 0.8]) sphere(6);
        }

        // Snout
        translate([5, 0, 17]) {
            scale([1.5, 0.4, 0.4]) sphere(4);
        }

        // Jade horns (3)
        for(angle = [0:120:240]) {
            rotate([0, 0, angle]) {
                translate([3, 0, 22]) {
                    rotate([30, 0, 0]) {
                        scale([0.3, 0.3, 1.2]) cylinder(5, 2, 0.5);
                    }
                }
            }
        }

        // Eyes (glowing)
        color("lime") {
            translate([3.5, 2.5, 19]) sphere(1.2);
            translate([3.5, -2.5, 19]) sphere(1.2);
        }
    }
}

// Wings with membrane structure
module wings() {
    color("darkolivegreen") {
        // Left wing
        translate([0, 6, 15]) {
            rotate([0, 0, 90]) {
                // Main wing bone
                rotate([30, 0, 0]) {
                    scale([0.3, 0.3, 1]) cylinder(12, 2, 1);
                }

                // Wing membrane segments
                for(i = [0:2]) {
                    rotate([20 + i * 15, 0, 0]) {
                        translate([0, 0, 8 + i * 2]) {
                            difference() {
                                scale([0.2, 0.1, 1]) cylinder(8 - i * 2, 2, 0.5);
                                translate([0, 0, -1]) cube([4, 4, 1], center = true);
                            }
                        }
                    }
                }
            }
        }

        // Right wing (mirror of left)
        mirror([0, 1, 0]) {
            translate([0, 6, 15]) {
                rotate([0, 0, 90]) {
                    rotate([30, 0, 0]) {
                        scale([0.3, 0.3, 1]) cylinder(12, 2, 1);
                    }

                    for(i = [0:2]) {
                        rotate([20 + i * 15, 0, 0]) {
                            translate([0, 0, 8 + i * 2]) {
                                difference() {
                                    scale([0.2, 0.1, 1]) cylinder(8 - i * 2, 2, 0.5);
                                    translate([0, 0, -1]) cube([4, 4, 1], center = true);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Legs with claws
module legs() {
    color("forestgreen") {
        // Front legs
        for(side = [-1, 1]) {
            translate([2, side * 3, 8]) {
                rotate([0, side * 15, 0]) {
                    // Upper leg
                    scale([0.4, 0.4, 0.8]) cylinder(5, 2, 1.5);

                    // Lower leg
                    translate([0, 0, -5]) {
                        rotate([30, 0, 0]) {
                            scale([0.3, 0.3, 0.8]) cylinder(4, 1.5, 1);

                            // Claws
                            for(claw = [-0.8, 0, 0.8]) {
                                translate([claw, 0, -4]) {
                                    scale([0.2, 0.2, 0.8]) cylinder(2, 0.5, 0.2);
                                }
                            }
                        }
                    }
                }
            }
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([-2, side * 2, 3]) {
                rotate([0, side * 20, 0]) {
                    scale([0.35, 0.35, 0.7]) cylinder(4, 1.8, 1.3);

                    translate([0, 0, -4]) {
                        rotate([20, 0, 0]) {
                            scale([0.3, 0.3, 0.7]) cylinder(3, 1.3, 0.8);

                            for(claw = [-0.6, 0, 0.6]) {
                                translate([claw, 0, -3]) {
                                    scale([0.2, 0.2, 0.7]) cylinder(1.5, 0.4, 0.2);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Jade scales decoration
module scales() {
    color("jade") {
        // Scales along back
        for(i = [0:3]) {
            translate([0, 0, 15 - i * 3]) {
                scale([1.2, 0.8, 0.3]) sphere(2);
            }
        }

        // Side scales
        for(side = [-1, 1]) {
            for(i = [0:4]) {
                translate([i * 1.5 - 3, side * 2, 12 - i * 2]) {
                    scale([0.8, 0.5, 0.2]) sphere(1.5);
                }
            }
        }
    }
}

// Assemble the creature
union() {
    body();
    head();
    wings();
    legs();
    scales();
}

// Kindlewhirl - Mythical fire-spiral dragon
// Realistic style, medium complexity
// 3D printable with no overhangs

$fa = 2;
$fs = 0.5;

module kindlewhirl() {
    color("orangered") union() {
        // Main body - elongated spiral
        translate([0, 0, 10]) rotate([90, 0, 0]) {
            for(i = [0:5:180]) {
                rotate([0, 0, i*2]) 
                    translate([i*0.15, 0, i*0.1]) 
                        scale([1, 1, 1.2]) 
                            sphere(d = 12 - i*0.05);
            }
        }

        // Head assembly
        translate([0, 0, 45]) rotate([0, 0, 0]) {
            // Snout
            color("darkorange") 
                translate([8, 0, -2]) 
                    scale([1.5, 1, 1]) 
                        sphere(d = 10);

            // Main head
            color("orangered") 
                sphere(d = 15);

            // Horns - curved back
            for(side = [-1, 1]) {
                color("goldenrod") 
                    translate([6, side*5, 8]) 
                        rotate([0, side*20, -30]) 
                            scale([0.3, 0.3, 1]) 
                                cylinder(h = 12, d1 = 4, d2 = 1);
            }

            // Eyes - glowing
            for(side = [-1, 1]) {
                color("yellow") 
                    translate([7, side*4, 2]) 
                        sphere(d = 2);
            }
        }

        // Wings - flame-shaped
        for(side = [-1, 1]) {
            color("orangered") 
                translate([0, side*8, 25]) 
                    rotate([0, 0, side*30]) {
                        // Upper wing segment
                        translate([5, 0, 5]) 
                            rotate([0, -20, 0]) 
                                scale([1.5, 0.2, 1]) 
                                    sphere(d = 12);

                        // Lower wing segment
                        translate([8, 0, -3]) 
                            rotate([0, 20, 0]) 
                                scale([1.2, 0.2, 0.8]) 
                                    sphere(d = 10);

                        // Wing membrane support
                        for(j = [0:2]) {
                            translate([j*3 + 3, 0, 2 - j*2]) 
                                rotate([0, -j*15, 0]) 
                                    cylinder(h = 8, d = 1.5);
                        }
                    }
        }

        // Tail - spiraling flame
        color("orangered") 
            translate([-25, 0, 5]) {
                for(i = [0:10:120]) {
                    rotate([0, 0, -i]) 
                        translate([-i*0.2, 0, -i*0.05]) 
                            scale([1 - i*0.003, 1 - i*0.005, 1 - i*0.002]) 
                                sphere(d = 8 - i*0.05);
                }
            }

        // Legs - sturdy for printing
        for(side = [-1, 1]) {
            color("darkred") 
                translate([-5, side*6, 0]) {
                    // Upper leg
                    rotate([0, -30, 0]) 
                        cylinder(h = 8, d = 6);

                    // Lower leg
                    translate([0, 0, -8]) 
                        rotate([0, 20, 0]) 
                            cylinder(h = 10, d1 = 6, d2 = 4);

                    // Foot
                    translate([3, 0, -17]) 
                        scale([1.5, 1, 0.5]) 
                            sphere(d = 6);
                }
        }

        // Flame ridges along spine
        color("goldenrod") 
            for(i = [0:15:180]) {
                rotate([0, 0, i]) 
                    translate([25 - i*0.2, 0, 10 + i*0.08]) 
                        scale([0.5, 0.5, 1]) 
                            sphere(d = 4);
            }

        // Ember scales
        color("red") 
            for(i = [0:20:360]) {
                for(j = [0:10:40]) {
                    rotate([0, 0, i]) 
                        translate([j - 10, 0, 5 + j*0.5]) 
                            sphere(d = 1.5);
                }
            }
    }
}

// Create the kindlewhirl
kindlewhirl();

// LunarWhirl - A mystical moon-phase dragon with swirling mane and crescent wings
// Designed for 3D printing with no overhangs >45 and 1.2mm wall thickness

color("LightSteelBlue") difference() {
    union() {
        // Central body with moon crater pattern
        body();

        // Crescent wings on sides
        translate([-20, 0, 5]) rotate([0, 0, -30]) crescent_wing();
        translate([20, 0, 5]) mirror([1, 0, 0]) rotate([0, 0, -30]) crescent_wing();

        // Swirling mane around neck
        translate([0, -12, 12]) rotate([90, 0, 0]) swirl_mane();

        // Lunar tail with phase rings
        translate([0, 18, 5]) rotate([90, 0, 0]) lunar_tail();

        // Four legs with moon boots
        translate([-8, 8, 0]) leg();
        translate([8, 8, 0]) leg();
        translate([-8, -8, 0]) leg();
        translate([8, -8, 0]) leg();

        // Horns with star tips
        translate([-5, -15, 15]) horn();
        translate([5, -15, 15]) mirror([1, 0, 0]) horn();
    }

    // Eye sockets
    translate([-5, -16, 10]) sphere(d=3);
    translate([5, -16, 10]) sphere(d=3);
}

// Main body with crater texture
module body() {
    difference() {
        color("Gainsboro") scale([1.3, 1, 1]) sphere(d=25);

        // Crater details
        for(i=[0:5]) {
            rotate([0, 0, i*60]) 
            translate([8, 0, 5]) 
            sphere(d=3);
        }

        // Neck cavity
        translate([0, -12, 5]) 
        rotate([15, 0, 0]) 
        cylinder(h=15, d=10);
    }
}

// Crescent wing with moon phase cutouts
module crescent_wing() {
    difference() {
        union() {
            // Main crescent
            color("Silver") 
            scale([1, 0.3, 0.1]) 
            rotate([0, 90, 0]) 
            cylinder(h=25, d=20);

            // Wing membrane support ribs
            for(i=[0:3]) {
                rotate([0, 0, i*30-45]) 
                translate([7, 0, 1]) 
                cube([15, 0.5, 2]);
            }
        }

        // Crescent cutout
        color("LightSteelBlue") 
        scale([1, 0.25, 0.1]) 
        rotate([0, 90, 0]) 
        translate([2, 0, 0]) 
        cylinder(h=30, d=15);
    }
}

// Swirling mane with star patterns
module swirl_mane() {
    color("GhostWhite") 
    for(i=[0:8]) {
        rotate([0, 0, i*45]) 
        translate([0, i*1.5, 0]) 
        rotate([0, 0, i*20]) 
        scale([1, 0.2, 1]) 
        sphere(d=6);
    }
}

// Lunar tail with phase rings
module lunar_tail() {
    color("Silver") 
    union() {
        // Main tail segments
        for(i=[0:5]) {
            translate([0, 0, -i*5]) 
            scale([1-i*0.1, 1-i*0.1, 1]) 
            cylinder(h=4, d=6);

            // Phase rings
            color("DimGray") 
            translate([0, 0, -i*5-2]) 
            cylinder(h=0.5, d=8-i, $fn=32);
        }
    }
}

// Leg with moon boot foot
module leg() {
    color("LightSlateGray") 
    union() {
        // Upper leg
        cylinder(h=12, d1=6, d2=4);

        // Lower leg
        translate([0, 0, -12]) 
        cylinder(h=10, d1=4, d2=5);

        // Moon boot foot
        translate([0, 0, -22]) 
        sphere(d=8);
    }
}

// Horn with star tip
module horn() {
    color("AliceBlue") 
    union() {
        // Main horn
        cylinder(h=10, d1=3, d2=1);

        // Star tip
        translate([0, 0, 10]) 
        scale([1, 1, 0.3]) 
        sphere(d=3);
    }
}

// Mossgleam - a luminous forest spirit with crystalline wings and mossy features
// Realistic style, medium complexity, 3D printable

// Scale factor for easy resizing
scale_factor = 0.8;

// Main module
module mossgleam() {
    scale([scale_factor, scale_factor, scale_factor])
    union() {
        // Main body - rounded torso with moss texture
        color([0.2, 0.4, 0.1]) 
        translate([0, 0, 15])
        sphere(d=35, $fn=32);

        // Head - slightly elongated with crystalline features
        color([0.3, 0.5, 0.2])
        translate([0, 0, 35])
        scale([0.8, 0.9, 1.2])
        sphere(d=20, $fn=32);

        // Eyes - glowing orbs
        for(x = [-6, 6]) {
            color([0.8, 1, 0.4])
            translate([x, -8, 38])
            sphere(d=4, $fn=16);
        }

        // Antler-like crystalline horns
        module horn() {
            color([0.6, 0.9, 1])
            union() {
                cylinder(h=25, d1=3, d2=1, $fn=8);
                translate([0, 0, 20])
                sphere(d=4, $fn=8);
            }
        }

        // Left horn
        translate([-12, -5, 45])
        rotate([0, 20, -30])
        horn();

        // Right horn
        translate([12, -5, 45])
        mirror([1, 0, 0])
        rotate([0, 20, -30])
        horn();

        // Wings - crystalline membrane structure
        module wing() {
            color([0.7, 0.95, 1, 0.7])
            difference() {
                // Main wing shape
                translate([-20, 0, 0])
                scale([2, 1, 0.5])
                sphere(d=30, $fn=16);

                // Cutouts for membrane pattern
                for(i = [1:3]) {
                    translate([-25 + i*8, -5 + i*2, 0])
                    rotate([0, 0, i*15])
                    scale([0.3, 0.8, 1])
                    cylinder(h=15, d=8, $fn=6);
                }
            }
        }

        // Left wing
        translate([-5, 20, 28])
        rotate([0, 0, 90])
        rotate([0, -20, 0])
        wing();

        // Right wing
        translate([5, 20, 28])
        mirror([1, 0, 0])
        rotate([0, 0, 90])
        rotate([0, -20, 0])
        wing();

        // Legs - moss-covered with small feet
        module leg() {
            color([0.25, 0.35, 0.15])
            union() {
                cylinder(h=20, d=6, $fn=12);
                translate([0, 0, -10])
                sphere(d=8, $fn=12);
            }
        }

        // Front legs
        for(x = [-10, 10]) {
            translate([x, 0, 15])
            leg();
        }

        // Back legs
        for(x = [-8, 8]) {
            translate([x, 15, 15])
            rotate([10, 0, 0])
            leg();
        }

        // Tail - segmented mossy appendage
        color([0.2, 0.45, 0.1])
        for(i = [1:5]) {
            translate([0, 20 + i*5, 15 - i*2])
            rotate([i*5, 0, 0])
            sphere(d=7 - i, $fn=12);
        }

        // Bioluminescent spots on body
        for(i = [1:8]) {
            angle = i * 45;
            radius = 15;
            color([0.7, 1, 0.5])
            translate([cos(angle) * radius, sin(angle) * radius, 20])
            sphere(d=2, $fn=8);
        }

        // Chest crystal
        color([0.8, 1, 1])
        translate([0, 5, 25])
        scale([1, 1.2, 0.8])
        rotate([0, 0, 45])
        cube([6, 6, 3], center=true);
    }
}

// Render the creature
mossgleam();

// Nimbuswhirl - Mythical Storm Spirit
// A swirling cloud-like creature with wings and lightning patterns

// Overall scale factor for printing
scale_factor = 0.4;

// Main body - swirling cloud mass
module body() {
    color([0.85, 0.85, 0.9]) {
        difference() {
            union() {
                // Central cloud sphere
                sphere(15);

                // Swirling cloud extensions
                for(i = [0:45:315]) {
                    rotate([0, 0, i])
                    translate([10, 0, 5])
                    scale([1.5, 0.8, 0.6])
                    sphere(8);
                }

                for(i = [22.5:45:337.5]) {
                    rotate([0, 0, i])
                    translate([8, 0, -3])
                    scale([1.2, 0.7, 0.5])
                    sphere(6);
                }
            }

            // Hollow out for printing
            translate([0, 0, -5])
            sphere(12);
        }
    }
}

// Lightning bolt element
module lightning_bolt(x, y, z, rot) {
    color([1, 1, 0.3]) {
        translate([x, y, z])
        rotate([0, 0, rot]) {
            // Main bolt
            cube([2, 1, 8]);
            translate([1, 0, 4])
            cube([3, 1, 6]);
            translate([0, 0, 9])
            cube([2, 1, 5]);

            // Side branches
            translate([0.5, 0, 2])
            rotate([0, 30, 0])
            cube([1, 1, 4]);
            translate([1.5, 0, 7])
            rotate([0, -30, 0])
            cube([1, 1, 3]);
        }
    }
}

// Wings - cloud-like with lightning veins
module wing(side) {
    color([0.8, 0.8, 0.95]) {
        mirror([side, 0, 0]) {
            translate([-5, 0, 5])
            rotate([0, -20, 0]) {
                // Main wing shape
                difference() {
                    hull() {
                        translate([-2, 0, 0])
                        sphere(3);
                        translate([-8, 0, 15])
                        sphere(2);
                        translate([-12, 0, 25])
                        sphere(1.5);
                        translate([-5, 0, 20])
                        sphere(2);
                    }

                    // Wing membrane hollow
                    translate([-6, -1, 10])
                    scale([0.6, 0.5, 0.8])
                    cube([10, 3, 15]);
                }

                // Lightning veins in wings
                lightning_bolt(-6, 0.5, 10, 20);
                lightning_bolt(-8, 0.5, 18, 35);
                lightning_bolt(-4, 0.5, 15, -10);
            }
        }
    }
}

// Storm cloud tail
module tail() {
    color([0.75, 0.75, 0.85]) {
        union() {
            for(i = [0:30:120]) {
                rotate([0, 0, -i])
                translate([-15 - i*0.3, 0, -5 - i*0.2])
                scale([1 - i*0.005, 1 - i*0.005, 1 - i*0.005])
                sphere(5 - i*0.03);
            }

            // Lightning sparks from tail
            lightning_bolt(-25, 0, -15, 45);
            lightning_bolt(-28, 2, -18, -30);
        }
    }
}

// Eyes - glowing storm orbs
module eyes() {
    color([0.3, 0.3, 1]) {
        // Left eye
        translate([5, 6, 5])
        sphere(2);

        // Right eye
        translate([5, -6, 5])
        sphere(2);

        // Eye glow
        color([0.5, 0.5, 1, 0.5]) {
            translate([6, 6, 5])
            sphere(1.5);
            translate([6, -6, 5])
            sphere(1.5);
        }
    }
}

// Horn - crystallized lightning
module horn() {
    color([0.9, 0.9, 1]) {
        translate([0, 0, 15])
        rotate([0, 0, 0]) {
            cylinder(h1 = 1, h2 = 0, r1 = 2, r2 = 0.5);
            translate([0, 0, 1])
            cylinder(h1 = 2, h2 = 0, r1 = 1.5, r2 = 0.3);
            translate([0, 0, 3])
            cylinder(h1 = 2, h2 = 0, r1 = 1, r2 = 0.1);
        }
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        body();
        wing(1);
        wing(-1);
        tail();
        eyes();
        horn();

        // Floating cloud wisps
        for(i = [0:60:359]) {
            rotate([0, 0, i])
            translate([18, 0, sin(i*3)*3])
            color([0.9, 0.9, 0.95, 0.7])
            sphere(1.5);
        }
    }
}

// Oakgleam - A mythical forest spirit that protects ancient oak groves
// Features: deer-like body, glowing antlers, leaf wings, bark texture

// Colors
$fa = 8;
$fs = 0.5;

// Main body module
module oakgleam_body() {
